CREATE OR REPLACE PROCEDURE obter_se_menuitem_visible (nr_seq_objeto_p bigint, cd_perfil_p bigint, cd_estabelecimento_p bigint, nm_usuario_regra_p text, ie_visible_p INOUT text) AS $body$
BEGIN

select IE_VISIBLE
into STRICT ie_visible_p
from FUNCAO_POPUP_REGRA
where IE_TIPO_REGRA = 'E'
      and nr_seq_objeto = nr_seq_objeto_p
      and coalesce(NM_USUARIO_REGRA, nm_usuario_regra_p) = nm_usuario_regra_p
      and coalesce(CD_PERFIL, cd_perfil_p) = cd_perfil_p
      and coalesce(CD_ESTABELECIMENTO, cd_estabelecimento_p) = cd_estabelecimento_p
order by nr_sequencia;

exception WHEN no_data_found THEN
ie_visible_p := '';

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
