CREATE OR REPLACE PROCEDURE obter_se_pac_fora_regra_idade ( cd_pessoa_p text) AS $body$
DECLARE


qt_anos_w	bigint;
qt_idade_min_w	bigint;
qt_idade_max_w	bigint;

BEGIN

if (cd_pessoa_p IS NOT NULL AND cd_pessoa_p::text <> '') then

	select	(coalesce(obter_idade_pf(cd_pessoa_p,clock_timestamp(),'A'),'0'))::numeric
	into STRICT	qt_anos_w
	;

	select  coalesce(max(qt_idade_min),0),
		coalesce(max(qt_idade_max),999)
	into STRICT	qt_idade_min_w,
		qt_idade_max_w
	from	parametro_agenda_quimio
	where	cd_estabelecimento = obter_estabelecimento_ativo
	and	nr_sequencia = (	SELECT 	max(nr_sequencia)
					from	parametro_agenda_quimio
					where	cd_estabelecimento = obter_estabelecimento_ativo
				);

	if (coalesce(qt_anos_w,0) > 0) and ((coalesce(qt_idade_max_w,0) > 0) or (coalesce(qt_idade_min_w,0) > 0)) then

		if (coalesce(qt_anos_w,0) > coalesce(qt_idade_max_w,999)) or (coalesce(qt_anos_w,0) < coalesce(qt_idade_min_w,0)) then
			CALL wheb_mensagem_pck.exibir_mensagem_abort(204414);
		end if;

	end if;

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
