CREATE OR REPLACE PROCEDURE obter_se_pf_prescr_rn ( cd_estabelecimento_p bigint, cd_pessoa_fisica_p text, nm_usuario_p text, ie_prescr_rn_p INOUT text, ds_prescr_rn_p INOUT text) AS $body$
DECLARE


ie_prescr_rn_w		varchar(1) 	:= 'S';
ds_prescr_rn_w		varchar(255)	:= null;
qt_idade_rn_w		smallint;
ie_sexo_w		varchar(15);
qt_idade_w		smallint;
ie_permite_pai_rn_w	varchar(1);


BEGIN
if (cd_estabelecimento_p IS NOT NULL AND cd_estabelecimento_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then

	begin
		qt_idade_rn_w := obter_param_usuario(924, 242, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, qt_idade_rn_w);
	exception
	when others then
		qt_idade_rn_w	:= 0;
	end;

	begin
		select	ie_sexo,
			(coalesce(obter_idade(dt_nascimento,coalesce(dt_obito,clock_timestamp()),'A'),0))::numeric
		into STRICT	ie_sexo_w,
			qt_idade_w
		from	pessoa_fisica
		where	cd_pessoa_fisica = cd_pessoa_fisica_p;
	exception
	when others then
		ie_sexo_w	:= 'X';
		qt_idade_w	:= -1;
	end;

	ie_permite_pai_rn_w := obter_param_usuario(924, 830, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_permite_pai_rn_w);

	if (qt_idade_rn_w > 0) and (qt_idade_w > -1) and (qt_idade_w < qt_idade_rn_w) then

		ie_prescr_rn_w	:= 'N';
		ds_prescr_rn_w	:= substr(obter_texto_tasy(457478, wheb_usuario_pck.get_nr_seq_idioma),1,255);

	elsif (ie_sexo_w <> 'X') and (ie_permite_pai_rn_w = 'N') and /* Criado parâmetro para permitir definir o pai na prescrição também (ex. Teste do pezinho)*/
		(ie_sexo_w <> 'F') then

		ie_prescr_rn_w	:= 'N';
		ds_prescr_rn_w	:= substr(obter_texto_tasy(457474, wheb_usuario_pck.get_nr_seq_idioma),1,255);

	else

		ie_prescr_rn_w	:= 'S';
		ds_prescr_rn_w	:= null;

	end if;

end if;

ie_prescr_rn_p	:= ie_prescr_rn_w;
ds_prescr_rn_p	:= ds_prescr_rn_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
