CREATE OR REPLACE PROCEDURE obter_se_quantidade_reg_limite ( nr_seq_tipo_avaliacao_p bigint, cd_pessoa_fisica_p bigint, nr_seq_med_avaliacao_pac_p bigint, nm_usuario_p text, ie_qt_acima_permitido_p INOUT text, ds_erro_p INOUT text) AS $body$
DECLARE

 
qt_max_reg_w		integer;
qt_registros_w		integer;			
						 

BEGIN 
ie_qt_acima_permitido_p	:= 'N';
 
select	qt_max_reg 
into STRICT	qt_max_reg_w 
from	med_tipo_avaliacao 
where	nr_sequencia	= nr_seq_tipo_avaliacao_p;
			 
if (qt_max_reg_w > 0) then 
	 
	select	count(*) 
	into STRICT	qt_registros_w 
	from	med_avaliacao_paciente 
    where	cd_pessoa_fisica	= cd_pessoa_fisica_p 
    and	nr_seq_tipo_avaliacao	= nr_seq_tipo_avaliacao_p 
    and	nr_sequencia     	<> nr_seq_med_avaliacao_pac_p;
	 
	if (qt_registros_w	>= qt_max_reg_w) then 
		ds_erro_p		:= substr(obter_texto_tasy(69725, wheb_usuario_pck.get_nr_seq_idioma),1,255) || ' ' || qt_max_reg_w || '.';
		ie_qt_acima_permitido_p	:= 'S';
	end if;
	 
end if;		
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_se_quantidade_reg_limite ( nr_seq_tipo_avaliacao_p bigint, cd_pessoa_fisica_p bigint, nr_seq_med_avaliacao_pac_p bigint, nm_usuario_p text, ie_qt_acima_permitido_p INOUT text, ds_erro_p INOUT text) FROM PUBLIC;

