CREATE OR REPLACE PROCEDURE obter_se_regra_disp_pepo (cd_material_p bigint, ie_origem_gasto_p text, ie_possui_regra_p INOUT text, ie_permite_alterar_p INOUT text, ds_origem_gasto_p INOUT text, ie_origem_padrao_p INOUT text) AS $body$
DECLARE



cd_grupo_material_w	smallint;
cd_subgrupo_material_w	smallint;
cd_classe_material_w	integer;
ie_permite_alterar_w	varchar(1);
ie_possui_regra_w		varchar(1) := 'N';
ds_origem_gasto_w		varchar(255);
ie_origem_gasto_w		varchar(255);

C01 CURSOR FOR
	SELECT 	ie_permite_alterar,
		obter_valor_dominio(1815,ie_origem_gasto),
		ie_origem_gasto
	from  	origem_gasto_material
	where	coalesce(cd_grupo_material,cd_grupo_material_w) 		= cd_grupo_material_w
	and	coalesce(cd_subgrupo_material,cd_subgrupo_material_w)	= cd_subgrupo_material_w
	and	coalesce(cd_classe_material,cd_classe_material_w) 		= cd_classe_material_w
	and	coalesce(cd_material,cd_material_p) 				= cd_material_p
	order by coalesce(cd_grupo_material,9999999999),
		coalesce(cd_subgrupo_material,9999999999),
		coalesce(cd_classe_material,9999999999),
		coalesce(cd_material,9999999999);


BEGIN

select	Obter_estrutura_material(cd_material,'G'),
	Obter_estrutura_material(cd_material,'S'),
	cd_classe_material
into STRICT	cd_grupo_material_w,
	cd_subgrupo_material_w,
	cd_classe_material_w
from	material
where	cd_material = cd_material_p;

open C01;
loop
fetch C01 into
	ie_permite_alterar_w,
	ds_origem_gasto_w,
	ie_origem_gasto_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_permite_alterar_w	:= ie_permite_alterar_w;
	ds_origem_gasto_w	:= ds_origem_gasto_w;
	ie_possui_regra_w	:= 'S';
	end;
end loop;
close C01;

if (ie_origem_gasto_w <> ie_origem_gasto_p) then
	ie_possui_regra_p		:= ie_possui_regra_w;
	ie_permite_alterar_p		:= ie_permite_alterar_w;
	ds_origem_gasto_p		:= ds_origem_gasto_w;
	ie_origem_padrao_p		:= ie_origem_gasto_w;
end if;

end	;
$body$
