CREATE OR REPLACE PROCEDURE obter_se_regra_just_atend_onc (nr_seq_regra_p bigint, nr_seq_atendimento_p bigint, nr_seq_material_p bigint, ie_existe_p INOUT text) AS $body$
DECLARE


ie_existe_w						varchar(1) := 'N';
qt_dose_prescricao_w			double precision;
qt_regra_justificada_w  		bigint;
cd_perfil_w						bigint;
ie_justificar_consistencia_w	varchar(255);
ie_forma_consistencia_w			varchar(255);
qt_regra_justif_regra_w  		bigint;


BEGIN
if (nr_seq_regra_p IS NOT NULL AND nr_seq_regra_p::text <> '') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (nr_seq_material_p IS NOT NULL AND nr_seq_material_p::text <> '')then

	cd_perfil_w	:= obter_perfil_ativo;

	Select  max(IE_JUSTIFICAR_CONSISTENCIA),
			max(ie_forma_consistencia)
	into STRICT	ie_justificar_consistencia_w,
			ie_forma_consistencia_w
	from	regra_consiste_onc_par
	where	nr_seq_regra = nr_seq_regra_p
	and		coalesce(cd_perfil,cd_perfil_w) = cd_perfil_w;

	if (coalesce(ie_justificar_consistencia_w,'N') = 'S') and (coalesce(ie_forma_consistencia_w,'S') = 'S') then

		Select 	max(qt_dose_prescricao)
		into STRICT	qt_dose_prescricao_w
		from	paciente_atend_medic
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and		nr_seq_material = nr_seq_material_p;


		Select 	count(*)
		into STRICT	qt_regra_justificada_w
		from	paciente_atend_erro_just
		where	nr_seq_atendimento = nr_seq_atendimento_p
		and		nr_seq_material = nr_seq_material_p
		and		qt_dose_prescricao = qt_dose_prescricao_w
		and		(ds_justif_incons_quimio IS NOT NULL AND ds_justif_incons_quimio::text <> '')
		and		nr_seq_regra = nr_seq_regra_p;

		if (qt_regra_justificada_w	= 0) then

			Select 	count(*)
			into STRICT	qt_regra_justif_regra_w
			from	paciente_atend_erro_just
			where	nr_seq_atendimento = nr_seq_atendimento_p
			and		nr_seq_material = nr_seq_material_p
			and		nr_seq_regra = nr_seq_regra_p;


			if (qt_regra_justif_regra_w > 0) then

				update 	paciente_atend_erro_just
				set		ds_justif_incons_quimio = '',
						qt_dose_prescricao = 0
				where	nr_seq_atendimento = nr_seq_atendimento_p
				and		nr_seq_material = nr_seq_material_p
				and		nr_seq_regra = nr_seq_regra_p;

				commit;

			ie_existe_w := 'N';

			end if;

		else

			ie_existe_w := 'S';

		end if;

	end if;

end if;

ie_existe_p :=  ie_existe_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
