SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_se_sobrep_horario_ant ( cd_agenda_p bigint, ds_hora_p timestamp, ie_sobreposicao_p INOUT text) AS $body$
DECLARE


ie_sobreposicao_w	varchar(1) := 'N';

hr_param_w			timestamp;
nr_seq_horario_w	bigint;
hr_agenda_w			timestamp;
nr_min_duracao_w	bigint;
hr_inicio_w			timestamp;
dt_agenda_w			timestamp;
ie_sobreposicao_encaixe_w	varchar(1) := 'S';


BEGIN

select	coalesce(max(ie_sobreposicao_encaixe),'S')
into STRICT	ie_sobreposicao_encaixe_w
from	parametro_agenda
where	cd_estabelecimento = obter_estabelecimento_ativo;

if (cd_agenda_p IS NOT NULL AND cd_agenda_p::text <> '') and (ds_hora_p IS NOT NULL AND ds_hora_p::text <> '') then

	dt_agenda_w		:= trunc(ds_hora_p);
	hr_inicio_w		:= ds_hora_p;


	/* obter horário/duração anterior */

	select	max(a.hr_inicio),
			max(a.nr_minuto_duracao),
			max(a.hr_inicio) + (max(a.nr_minuto_duracao) - 1) / 1440
	into STRICT	hr_agenda_w,
			nr_min_duracao_w,
			hr_param_w
	from	agenda_paciente a
	where	a.cd_agenda	 		= cd_agenda_p
	and		a.ie_status_agenda 	not in ('C','L','II')
	and		a.dt_agenda			between dt_agenda_w and fim_dia(dt_agenda_w)
	and		a.nr_sequencia		=(	SELECT	max(x.nr_sequencia)
									from	agenda_paciente x
									where	x.cd_agenda					= a.cd_agenda
									and		x.hr_inicio					< hr_inicio_w
									and		x.ie_status_agenda 			not in ('C','L','II')
									and		((ie_sobreposicao_encaixe_w = 'N' and ie_encaixe <> 'S' ) or ie_sobreposicao_encaixe_w = 'S')
									and		x.dt_agenda					between dt_agenda_w and fim_dia(dt_agenda_w)
								 );

	/* verificar sobreposição */

	if (hr_agenda_w IS NOT NULL AND hr_agenda_w::text <> '') then

		if (hr_inicio_w <= hr_param_w) then
			ie_sobreposicao_w := 'S';
		else
			ie_sobreposicao_w := 'N';
		end if;
	else
		ie_sobreposicao_w := 'N';
	end if;

end if;

ie_sobreposicao_p	:= ie_sobreposicao_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
