CREATE OR REPLACE PROCEDURE obter_se_validade_vencer_nf ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text, ds_lista_mat_p INOUT text) AS $body$
DECLARE


qt_dias_validade_w			varchar(10);
ie_permite_calcular_w		varchar(10);
nr_item_nf_w			integer;
cd_material_w			integer;
dt_validade_w			timestamp;
ds_justificativa_w			varchar(255);
ds_lista_mat_w			varchar(255);

c01 CURSOR FOR
SELECT	cd_material,
	trunc(dt_validade,'dd'),
	ds_justificativa
from	nota_fiscal_item
where	nr_sequencia = nr_sequencia_p
and	(dt_validade IS NOT NULL AND dt_validade::text <> '')

union all

select	a.cd_material,
	trunc(b.dt_validade,'dd'),
	a.ds_justificativa
from	nota_fiscal_item a,
	nota_fiscal_item_lote b
where	a.nr_Sequencia = b.nr_Seq_nota
and	a.nr_item_nf = b.nr_item_nf
and	a.nr_sequencia = nr_sequencia_p
and	(b.dt_validade IS NOT NULL AND b.dt_validade::text <> '');


BEGIN

select	Obter_Valor_Param_Usuario(40, 230, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p),
	Obter_Valor_Param_Usuario(40, 231, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p)
into STRICT	qt_dias_validade_w,
	ie_permite_calcular_w
;

if (qt_dias_validade_w IS NOT NULL AND qt_dias_validade_w::text <> '') then

	open C01;
	loop
	fetch C01 into
		cd_material_w,
		dt_validade_w,
		ds_justificativa_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		if (ie_permite_calcular_w = 'N') and
			(dt_validade_w <= (trunc(clock_timestamp(),'dd') + qt_dias_validade_w)) then
			ds_lista_mat_w	:= substr(ds_lista_mat_w || cd_material_w || ', ',1,255);

		elsif (ie_permite_calcular_w = 'J') and (coalesce(ds_justificativa_w::text, '') = '') and
			(dt_validade_w <= (trunc(clock_timestamp(),'dd') + qt_dias_validade_w)) then
			ds_lista_mat_w	:= substr(ds_lista_mat_w || cd_material_w || ', ',1,255);
		end if;
		end;
	end loop;
	close C01;

end if;

ds_lista_mat_p			:= substr(ds_lista_mat_w,1,length(ds_lista_mat_w) -2);

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
