CREATE OR REPLACE PROCEDURE obter_situacao_extrato_js ( nr_seq_conta_p bigint, nr_documento_p text, vl_extrato_p bigint, dt_movto_p text, ie_deb_cred_p text, ie_imp_extrato_dup_p INOUT text, qt_registros_p INOUT bigint) AS $body$
DECLARE

ie_imp_extrato_dup_w varchar(1);
qt_registros_w 	integer;

BEGIN
select 	ie_imp_extrato_dup
into STRICT	ie_imp_extrato_dup_w
from 	banco_estabelecimento_v
where 	nr_sequencia = nr_seq_conta_p;

select 	count(*)
into STRICT 	qt_registros_w
from 	banco_extrato_lanc
where 	nr_documento = nr_documento_p
and 	vl_lancamento = (vl_extrato_p / 100)
and 	dt_movimento = to_date(dt_movto_p, 'dd/mm/yyyy')
and 	ie_deb_cred = ie_deb_cred_p;
ie_imp_extrato_dup_p := ie_imp_extrato_dup_w;
qt_registros_p := qt_registros_w;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
