CREATE OR REPLACE PROCEDURE obter_substancia_proc_lab ( nr_atendimento_p bigint, nr_seq_exame_p bigint, ie_substancia_p INOUT text, ds_substancia_p INOUT text) AS $body$
DECLARE


cd_material_w		integer;
nr_prescricao_w		bigint;
nr_sequencia_w		bigint;
ds_material_w		varchar(255);
qt_dose_w		double precision;
cd_unidade_medida_w	varchar(30);
ds_intervalo_w		varchar(255);

C01 CURSOR FOR
	SELECT	cd_material
	from	substancia_exame_lab
	where	nr_seq_exame = nr_seq_exame_p
	order by 1;

BEGIN

ds_substancia_p := '';
ie_substancia_p := 'N';

open C01;
loop
fetch C01 into
	cd_material_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	ie_substancia_p := 'S';

	select	max(a.nr_prescricao)
	into STRICT	nr_prescricao_w
	from	prescr_medica a,
		prescr_material b
	where	a.nr_atendimento = nr_atendimento_p
	and	a.nr_prescricao  = b.nr_prescricao
	and	b.cd_material	 = cd_material_w;

	if (coalesce(ds_substancia_p::text, '') = '') and (nr_prescricao_w IS NOT NULL AND nr_prescricao_w::text <> '') then

		select	max(nr_sequencia)
		into STRICT	nr_sequencia_w
		from	prescr_material
		where	nr_prescricao	 = nr_prescricao_w
		and	cd_material	 = cd_material_w;

		select	substr(Obter_desc_material(cd_material),1,80),
			coalesce(qt_dose,0),
			coalesce(cd_unidade_medida_dose,cd_unidade_medida),
			substr(obter_desc_intervalo(cd_intervalo),1,50)
		into STRICT	ds_material_w,
			qt_dose_w,
			cd_unidade_medida_w,
			ds_intervalo_w
		from	prescr_material
		where	nr_prescricao	= nr_prescricao_w
		and	nr_sequencia	= nr_sequencia_w;

		ds_substancia_p := substr(ds_material_w ||' '|| qt_dose_w ||' '|| cd_unidade_medida_w ||' '||ds_intervalo_w,1,255);
	end if;

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
