CREATE OR REPLACE PROCEDURE obter_tipo_adm_grafico_pepo ( nr_grafico_atual_p bigint, ie_tipo_adm_p INOUT text) AS $body$
DECLARE


ie_tipo_w	smallint;
/* H - Hemocomponente | IN - Intermitente | CO - Cont√≠nuo */

ie_tipo_adm_w	varchar(2);


BEGIN


select  max(ie_tipo)
into STRICT	ie_tipo_w
from    cirurgia_agente_anestesico
where   nr_sequencia = nr_grafico_atual_p
and	coalesce(ie_situacao,'A') = 'A';

if (coalesce(ie_tipo_w, 0) = 5) then
	begin
	ie_tipo_adm_w := 'H';
	end;
elsif (obter_modo_medicamento_cirur(nr_grafico_atual_p) = 'IN') then
	begin
	ie_tipo_adm_w := 'IN';
	end;
elsif (obter_modo_medicamento_cirur(nr_grafico_atual_p) = 'CO') then
	begin
	ie_tipo_adm_w := 'CO';
	end;
end if;

ie_tipo_adm_p := ie_tipo_adm_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
