SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_tipo_classif_diag (nr_atendimento_p bigint, ie_tipo_diag_p INOUT bigint, ie_classif_doenca_p INOUT text) AS $body$
DECLARE


qt_reg_w            bigint;
ie_tipo_diag_w      smallint := 0;
ie_classif_doenca_w varchar(1) := '';
ie_lib_diag_medico_w     varchar(1);
cd_estabelecimento_w     smallint;


BEGIN

cd_estabelecimento_w := wheb_usuario_pck.get_cd_estabelecimento;

Select    coalesce(max(ie_lib_diag_medico),'N')
into STRICT      ie_lib_diag_medico_w
from      parametro_medico
where     cd_estabelecimento = cd_estabelecimento_w;

   select count(*)
   into STRICT   qt_reg_w
   from   DIAGNOSTICO_DOENCA
   where  nr_atendimento = nr_atendimento_p
   and    coalesce(dt_inativacao::text, '') = ''
   and    ie_tipo_diagnostico = 2
   and    ((ie_lib_diag_medico_w = 'N') or (dt_liberacao IS NOT NULL AND dt_liberacao::text <> ''))
   and    ie_classificacao_doenca = 'P';

   if (qt_reg_w = 0) then

      ie_tipo_diag_w      := 2;
      ie_classif_doenca_w := 'P';

   else

        ie_tipo_diag_w      := 1;
        ie_classif_doenca_w := 'S';

   end if;

ie_tipo_diag_p := ie_tipo_diag_w;
ie_classif_doenca_p := ie_classif_doenca_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
