CREATE OR REPLACE PROCEDURE obter_tipo_classif_proc_aih ( cd_procedimento_p bigint, ie_classif_p INOUT text, ie_tipo_p INOUT text) AS $body$
DECLARE



ie_tipo_item_w		smallint;
ds_valor_item_w		varchar(20);


c01 CURSOR FOR
	SELECT	ie_tipo_item,
		ds_valor_item
	from	procedimento_consiste_sus
	where 	cd_procedimento 	= cd_procedimento_p
	and 	ie_origem_proced	= 2
	order by	1;


BEGIN

open c01;
	loop
	fetch c01 into
		ie_tipo_item_w,
		ds_valor_item_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin
		if (ie_tipo_item_w = 1) then
			begin
			if (coalesce(ie_classif_p::text, '') = '') then
				ie_classif_p	:= ds_valor_item_w;
			else
				ie_classif_p	:= ie_classif_p || ', ' || ds_valor_item_w;
			end if;
			end;
		elsif (ie_tipo_item_w = 2) then
			begin
			if (coalesce(ie_tipo_p::text, '') = '') then
				ie_tipo_p	:= ds_valor_item_w;
			else
				ie_tipo_p	:= ie_tipo_p || ', ' || ds_valor_item_w;
			end if;
			end;
		end if;
		end;
	end loop;
close c01;

commit;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
