CREATE OR REPLACE PROCEDURE obter_tipo_local_regra ( nr_atendimento_p bigint, nr_seq_local_p INOUT bigint, ie_tipo_p INOUT text) AS $body$
DECLARE

 
nr_seq_regra_w			bigint;
cd_setor_atendimento_w		integer;
									

BEGIN 
 
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
 
	select	obter_setor_atendimento(nr_atendimento_p) 
	into STRICT	cd_setor_atendimento_w 
	;
 
	select	coalesce(max(nr_Sequencia),0) 
	into STRICT	nr_seq_regra_w 
	from	regra_pront_gestao_setor 
	where	cd_setor_atendimento = cd_setor_atendimento_w;
		 
	if (nr_seq_regra_w > 0) then 
		 
		select	nr_seq_local, 
			ie_tipo 
		into STRICT	nr_seq_local_p, 
			ie_tipo_p 
		from	regra_pront_gestao_setor 
		where	nr_sequencia = nr_seq_regra_w;		
			 
	end if;
	 
end if;
 
end;
