CREATE OR REPLACE PROCEDURE obter_ultima_compra_material ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_unidade_medida_p text, ie_unidade_medida_p text, dt_inicio_p timestamp, nr_sequencia_p INOUT bigint, vl_ultima_Compra_p INOUT bigint) AS $body$
DECLARE


cd_unid_medida_w			varchar(30);
nr_sequencia_w			bigint;
vl_ultima_compra_w		double precision;
cd_material_estoque_w		integer;

c01 CURSOR FOR
SELECT a.nr_sequencia,
	dividir(coalesce(a.vl_liquido,0), coalesce(qt_item_nf,1))
from	nota_fiscal b,
	nota_fiscal_item a
where	a.nr_sequencia		= b.nr_sequencia
and	b.ie_acao_nf		= '1'
and	b.ie_situacao		= '1'
and	b.cd_estabelecimento	= cd_estabelecimento_p
and	a.cd_material		= cd_material_p
and	a.cd_unidade_medida_compra 	= cd_unid_medida_w
and	b.dt_entrada_saida		>= dt_inicio_p
order by 1;


BEGIN

if (cd_unidade_medida_p IS NOT NULL AND cd_unidade_medida_p::text <> '') then
	cd_unid_medida_w	:= cd_unidade_medida_p;
else
	select	CASE WHEN ie_unidade_medida_p='C' THEN  substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,30) WHEN ie_unidade_medida_p='E' THEN  substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMC'),1,30)  ELSE substr(obter_dados_material_estab(cd_material,cd_estabelecimento_p,'UMS'),1,30) END
	into STRICT	cd_unid_medida_w
	from	material
	where	cd_material = cd_material_p;
end if;

nr_sequencia_p	:= 0;
vl_ultima_compra_p	:= 0;
OPEN C01;
LOOP
FETCH C01 into
	nr_sequencia_w,
	vl_ultima_compra_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	nr_sequencia_p	:= nr_sequencia_w;
	vl_ultima_compra_p	:= vl_ultima_compra_w;
END LOOP;
CLOSE C01;

if (vl_ultima_compra_p = 0) and (nr_sequencia_p = 0) then

	select	cd_material_estoque
	into STRICT	cd_material_estoque_w
	from	material
	where	cd_material = cd_material_p;

	select	coalesce(max(vl_preco_ult_compra),0)
	into STRICT	vl_ultima_compra_p
	from	saldo_estoque
	where	cd_material = cd_material_estoque_w
	and	cd_estabelecimento = cd_estabelecimento_p
	and	dt_mesano_referencia = PKG_DATE_UTILS.start_of(clock_timestamp(), 'month', 0);


end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
