SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE obter_ult_indic_agenda_serv ( cd_pessoa_fisica_p text, ie_opcao_p text, dt_atual_p timestamp, nr_seq_indicacao_p INOUT bigint, cd_pessoa_indicacao_p INOUT text) AS $body$
DECLARE


nr_seq_agenda_w			agenda_consulta.nr_sequencia%type;
nr_seq_agenda_cons_w	agenda_consulta.cd_pessoa_fisica%type;
nr_seq_agenda_exame_w	agenda_paciente.cd_pessoa_fisica%type;
nr_seq_indicacao_w		bigint;
cd_pessoa_indicacao_w	varchar(10);
dt_max_consulta_w		timestamp;
dt_max_exames_w			timestamp;


BEGIN

if (ie_opcao_p = 'U') then
	if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
		begin	
		select	max(nr_sequencia)
		into STRICT	nr_seq_agenda_w
		from	agenda_consulta
		where	cd_pessoa_fisica = cd_pessoa_fisica_p
		and		obter_tipo_agenda(cd_agenda) = 5;
		
		if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
			select	nr_seq_indicacao,
					cd_pessoa_indicacao
			into STRICT	nr_seq_indicacao_w,
					cd_pessoa_indicacao_w
			from	agenda_consulta
			where	nr_sequencia = nr_seq_agenda_w;
		end if;
		end;
	end if;
elsif (ie_opcao_p = 'I') then /*A partir do ultimo agendamento, independente da agenda e do status*/

		
		/*AGENDA_CONSULTA*/

		select	max(nr_sequencia)
		into STRICT	nr_seq_agenda_cons_w				
		from	agenda_consulta
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and		dt_agenda			< dt_atual_p
		and		ie_status_agenda	<> 'L'
		and		(nr_seq_indicacao IS NOT NULL AND nr_seq_indicacao::text <> '');
		
		/*AGENDA_PACIENTE*/

		select	max(nr_sequencia)				
		into STRICT	nr_seq_agenda_exame_w				
		from	agenda_paciente
		where	cd_pessoa_fisica 	= cd_pessoa_fisica_p
		and		dt_agenda			< dt_atual_p
		and		ie_status_agenda	<> 'L'
		and		(nr_seq_indicacao IS NOT NULL AND nr_seq_indicacao::text <> '');
				
		if (nr_seq_agenda_cons_w IS NOT NULL AND nr_seq_agenda_cons_w::text <> '') or (nr_seq_agenda_exame_w IS NOT NULL AND nr_seq_agenda_exame_w::text <> '')then
			
			select	max(dt_agenda)
			into STRICT	dt_max_consulta_w
			from	agenda_consulta
			where	nr_sequencia = nr_seq_agenda_cons_w;
			
			select	max(hr_inicio)
			into STRICT	dt_max_exames_w
			from	agenda_paciente
			where	nr_sequencia = nr_seq_agenda_exame_w;
			
			if ((dt_max_consulta_w IS NOT NULL AND dt_max_consulta_w::text <> '') and coalesce(dt_max_exames_w::text, '') = '') or (dt_max_consulta_w > dt_max_exames_w)then
				select	max(nr_seq_indicacao),
						max(cd_pessoa_indicacao)
				into STRICT	nr_seq_indicacao_w,
						cd_pessoa_indicacao_w
				from	agenda_consulta
				where	nr_sequencia = nr_seq_agenda_cons_w;
			else
				select	max(nr_seq_indicacao),
						max(cd_pessoa_indicacao)
				into STRICT	nr_seq_indicacao_w,
						cd_pessoa_indicacao_w
				from	agenda_paciente
				where	nr_sequencia = nr_seq_agenda_exame_w;			
			end if;					
		
		end if;
		
		
end if;

nr_seq_indicacao_p		:= nr_seq_indicacao_w;
cd_pessoa_indicacao_p	:= cd_pessoa_indicacao_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_ult_indic_agenda_serv ( cd_pessoa_fisica_p text, ie_opcao_p text, dt_atual_p timestamp, nr_seq_indicacao_p INOUT bigint, cd_pessoa_indicacao_p INOUT text) FROM PUBLIC;

