CREATE OR REPLACE PROCEDURE obter_ult_procedencia_agenda ( cd_pessoa_fisica_p text, cd_procedencia_p INOUT text) AS $body$
DECLARE


nr_seq_agenda_w		agenda_consulta.cd_pessoa_fisica%type;
cd_procedencia_w	varchar(10);


BEGIN

if (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then
	begin	
	select	max(nr_sequencia)
	into STRICT	nr_seq_agenda_w
	from	agenda_consulta
	where	cd_pessoa_fisica = cd_pessoa_fisica_p
	and	obter_tipo_agenda(cd_agenda) in (3,4);
	
	if (nr_seq_agenda_w IS NOT NULL AND nr_seq_agenda_w::text <> '') then
		select	cd_procedencia
		into STRICT	cd_procedencia_w
		from	agenda_consulta
		where	nr_sequencia = nr_seq_agenda_w;
	end if;
	end;
end if;

cd_procedencia_p	:= cd_procedencia_w;

end;
