CREATE OR REPLACE PROCEDURE obter_usuarios_concorrentes () AS $body$
DECLARE


nr_sequencia_w		bigint;
qt_tasy_w		bigint;
dt_log_w			timestamp;
ds_hash_w		varchar(32);


BEGIN

dt_log_w	:= clock_timestamp();

/* UsuÃ¡rio conectados no TASY*/

SELECT	COUNT(*)
INTO STRICT	qt_tasy_w
FROM	tasy_controle_login
WHERE
trunc(dt_acesso) = trunc(clock_timestamp())
and ds_aplicacao = 'TasySwing'
and coalesce(dt_saida::text, '') = '';

ds_hash_w := RAWTOHEX(dbms_obfuscation_toolkit.md5(input => encode('TASY_JAVA' || qt_tasy_w || to_char(dt_log_w,'dd/mm/yyyy hh24:mi:ss'::bytea, 'hex')::bytea)));

select	nextval('tasy_controle_login_seq')
into STRICT	nr_sequencia_w
;

insert into TASY_USUARIOS_CONCORRENTES(
	NR_SEQUENCIA,
	DT_LOG,
	QT_TASY,
	DS_HASH)
values (
	nr_sequencia_w,
	dt_log_w,
	qt_tasy_w,
	ds_hash_w);

commit;

END;
