CREATE OR REPLACE PROCEDURE obter_validacao_nivel (nr_seq_nivel_p bigint, nr_sequencia_p bigint, nr_seq_regra_p bigint, nm_usuario_lib_p text) AS $body$
DECLARE


qt_nivel_existente_w regra_aprov_cobr_usuario.qt_nivel_liberacao%type;
qt_sugestao_nivel_w regra_aprov_cobr_usuario.qt_nivel_liberacao%type;
qt_nm_usuario_lib_w 	bigint;
ds_mensagem_w 		varchar(255);


BEGIN

select	count(*)
into STRICT	qt_nivel_existente_w
from	regra_aprov_cobr_usuario a
where	a.qt_nivel_liberacao = nr_seq_nivel_p
and 	a.nr_sequencia <> nr_sequencia_p
and 	a.nr_Seq_regra = nr_seq_regra_p;

select	count(*)
into STRICT	qt_nm_usuario_lib_w
from	regra_aprov_cobr_usuario a
where	a.nr_seq_regra		= nr_seq_regra_p
and 	a.nr_sequencia		 <> nr_sequencia_p
and	a.nm_usuario_lib	= nm_usuario_lib_p;

if (qt_nivel_existente_w > 0)then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(334594);
end if;

if (qt_nm_usuario_lib_w > 0) then
	ds_mensagem_w := wheb_mensagem_pck.get_texto(335861, 'nm_usuario=' || nm_usuario_lib_p);
	CALL wheb_mensagem_pck.exibir_mensagem_abort(ds_mensagem_w);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
