CREATE OR REPLACE PROCEDURE obter_valores_comunicado ( nr_atendimento_p bigint, nr_interno_conta_p bigint, nm_usuario_p text, nm_pessoa_atend_p INOUT text, vl_conta_p INOUT text) AS $body$
DECLARE

 
nm_pessoa_atend_w	varchar(100);
vl_conta_w		double precision;

BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') and (nr_interno_conta_p IS NOT NULL AND nr_interno_conta_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') 
	then 
	begin 
	nm_pessoa_atend_w := obter_pessoa_atendimento( nr_atendimento_p,'N' );
	 
	select round(( obter_valor_conta( a.nr_interno_conta,0 ))::numeric,2) 
	into STRICT	vl_conta_w 
	from  conta_paciente a 
	where  a.nr_interno_conta = nr_interno_conta_p;
	end;
end if;
nm_pessoa_atend_p	:= nm_pessoa_atend_w;
vl_conta_p 		:= campo_mascara_virgula(vl_conta_w);
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
