CREATE OR REPLACE PROCEDURE obter_valores_diet_cpoe_npt_md (qt_kcal_proteina_p bigint, qt_kcal_carboidrato_p bigint, qt_kcal_lipidio_p bigint, qt_dieta_qt_kcal_lipidio_p bigint, qt_dieta_qt_kcal_proteina_p bigint, qt_peso_p bigint, pr_conc_proteina_solucao_p bigint, qt_volume_diario_p bigint, qt_kcal_total_p INOUT bigint, pr_lipidio_p INOUT bigint, pr_proteina_p INOUT bigint, pr_carboidrato_p INOUT bigint, qt_kcal_kg_p INOUT bigint, qt_kcal_nao_proteico_p INOUT bigint, qt_grama_nitrogenio_p INOUT bigint, qt_rel_cal_nit_p INOUT bigint, qt_g_proteina_kg_dia_p INOUT bigint, qt_gotejo_npt_p INOUT bigint) AS $body$
BEGIN

  qt_kcal_total_p := (coalesce(qt_kcal_proteina_p, 0) + coalesce(qt_kcal_carboidrato_p, 0) + coalesce(qt_kcal_lipidio_p, 0));

