  qt_kcal_kg_p := (coalesce(qt_kcal_total_p, 0) + coalesce(qt_peso_p, 0));

  qt_kcal_nao_proteico_p := (coalesce(qt_kcal_carboidrato_p, 0) + coalesce(qt_kcal_lipidio_p, 0));

