CREATE OR REPLACE PROCEDURE obter_valores_digitacao ( nr_titulo_p bigint, vl_saldo_titulo_p bigint, vl_cheque_p INOUT bigint) AS $body$
DECLARE


vl_juros_atual_w	double precision;
vl_multa_atual_w	double precision;
vl_cheque_w		double precision;


BEGIN

vl_juros_atual_w := obter_juros_multa_titulo(nr_titulo_p, clock_timestamp(), 'P', 'J');
vl_multa_atual_w := obter_juros_multa_titulo(nr_titulo_p, clock_timestamp(), 'P', 'M');
vl_cheque_w := vl_saldo_titulo_p + vl_juros_atual_w + vl_multa_atual_w;

vl_cheque_p := vl_cheque_w;

end;
$body$
