CREATE OR REPLACE PROCEDURE obter_valores_imprimir_pa ( nr_atendimento_p bigint, nr_seq_vaga_p INOUT bigint, nr_seq_autorizacao_p INOUT bigint) AS $body$
DECLARE


nr_seq_vaga_w		bigint := -1;
nr_seq_autorizacao_w	bigint := -1;


BEGIN
if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then
	begin
	select	coalesce(max(nr_sequencia),-1)
	into STRICT	nr_seq_vaga_w
	from	gestao_vaga
	where	nr_atendimento	= nr_atendimento_p;

	if (nr_seq_vaga_w > 0) then
		begin
		select	min(nr_sequencia)
		into STRICT	nr_seq_autorizacao_w
		from	autorizacao_convenio
		where	nr_seq_gestao	= nr_seq_vaga_w;
		end;
	end if;
	end;
end if;
nr_seq_vaga_p		:= nr_seq_vaga_w;
nr_seq_autorizacao_p	:= nr_seq_autorizacao_w;
commit;
end;
