CREATE OR REPLACE PROCEDURE obter_valores_npt_adulta ( cd_pessoa_p text, nr_prescr_p bigint, qt_dia_npt_p INOUT bigint, qt_altura_p INOUT bigint, qt_ano_p INOUT text, qt_mes_p INOUT text, qt_dia_p INOUT text) AS $body$
DECLARE


qt_dia_npt_w	bigint;
qt_altura_w	double precision;
qt_ano_w	varchar(10);
qt_mes_w	varchar(10);
qt_dia_w	varchar(10);

BEGIN

if (cd_pessoa_p IS NOT NULL AND cd_pessoa_p::text <> '') then
	begin
	Select	coalesce(qt_altura_cm,0),
		substr(obter_idade(dt_nascimento,clock_timestamp(),'A'),1,5),
		substr(obter_idade(dt_nascimento,clock_timestamp(),'MM'),1,5),
		substr(obter_idade(dt_nascimento,clock_timestamp(),'DI'),1,5)
	into STRICT	qt_altura_w,
		qt_ano_w,
		qt_mes_w,
		qt_dia_w
	from	pessoa_fisica
	where	cd_pessoa_fisica = cd_pessoa_p;
	end;
end if;

if (nr_prescr_p IS NOT NULL AND nr_prescr_p::text <> '') then
	begin
	select	count(distinct(trunc(dt_prescricao))) + 1
	into STRICT	qt_dia_npt_w
	from 	prescr_medica b,
		nut_pac a
	where	a.nr_prescricao = b.nr_prescricao
	and	trunc(b.dt_prescricao) <> trunc(clock_timestamp())
	and	b.nr_atendimento = (SELECT obter_atendimento_prescr(nr_prescr_p) );
	end;
end if;

qt_dia_npt_p	:= qt_dia_npt_w;
qt_altura_p	:= qt_altura_w;
qt_ano_p	:= qt_ano_w;
qt_mes_p	:= qt_mes_w;
qt_dia_p	:= qt_dia_w;

commit;

end;
$body$
LANGUAGE PLPGSQL
