CREATE OR REPLACE PROCEDURE obter_valores_prescr_result ( nr_prescricao_p bigint, ie_result_atend_p text, qt_exames_p INOUT bigint, qt_exames_sem_result_p INOUT bigint) AS $body$
DECLARE


qt_exames_w			bigint;
qt_exames_sem_result_w		bigint;


BEGIN

if (ie_result_atend_p = 'S') then

	select 	count(*)
	into STRICT	qt_exames_w
	from 	prescr_procedimento a,
		prescr_medica b, prescr_medica c
	where 	a.nr_prescricao = b.nr_prescricao
	and 	b.nr_atendimento = c.nr_atendimento
	and 	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
	and 	coalesce(a.ie_suspenso,'N') = 'N'
	and 	obter_tipo_baixa_prescr(a.cd_motivo_baixa) = 'N'
	and 	c.nr_prescricao = nr_prescricao_p;

elsif (ie_result_atend_p = 'N') then

	select 	count(*)
	into STRICT	qt_exames_w
	from 	prescr_procedimento a
	where 	(a.nr_seq_exame IS NOT NULL AND a.nr_seq_exame::text <> '')
	and 	coalesce(a.ie_suspenso,'N') = 'N'
	and 	obter_tipo_baixa_prescr(a.cd_motivo_baixa) = 'N'
	and 	a.nr_prescricao = nr_prescricao_p;
end if;

select 	coalesce(count(*),0)
into STRICT	qt_exames_sem_result_w
from 	prescr_procedimento a,
	exame_lab_result_item b,
	exame_lab_resultado c
where  	c.nr_prescricao = a.nr_prescricao
and	b.nr_seq_prescr = a.nr_sequencia
and	b.nr_seq_resultado = c.nr_seq_resultado
and  	obter_tipo_baixa_prescr(a.cd_motivo_baixa) = 'N'
and	a.ie_status_atend < 30
and	(b.nr_seq_material IS NOT NULL AND b.nr_seq_material::text <> '')
and	a.nr_prescricao = nr_prescricao_p;

qt_exames_p		:= qt_exames_w;
qt_exames_sem_result_p	:= qt_exames_sem_result_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE obter_valores_prescr_result ( nr_prescricao_p bigint, ie_result_atend_p text, qt_exames_p INOUT bigint, qt_exames_sem_result_p INOUT bigint) FROM PUBLIC;

