CREATE OR REPLACE PROCEDURE obter_valores_tit_classif ( nr_titulo_p bigint, vl_total_rateio_p INOUT bigint, vl_dif_rateio_p INOUT bigint) AS $body$
DECLARE


vl_total_rateio_w	double precision;
vl_dif_rateio_w		double precision;


BEGIN

select  coalesce(sum(vl_titulo),0)
into STRICT 	vl_total_rateio_w
from    titulo_pagar_classif
where   nr_titulo	= nr_titulo_p;
	
select  (obter_dados_tit_pagar(nr_titulo, 'VT'))::numeric
into STRICT	vl_dif_rateio_w
from  	titulo_pagar
where   nr_titulo	= nr_titulo_p;

vl_total_rateio_p 	:= vl_total_rateio_w;
vl_dif_rateio_p   	:= vl_dif_rateio_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
