CREATE OR REPLACE PROCEDURE obter_valor_ult_compra_conv ( cd_estabelecimento_p bigint, cd_material_p bigint, cd_unidade_medida_p text, vl_retorno_p INOUT bigint) AS $body$
DECLARE


vl_ultima_compra_w	double precision := 0;

cd_unidade_medida_estoque_w	varchar(30);
cd_unidade_medida_compra_w	varchar(30);
cd_unidade_medida_consumo_w	varchar(30);
ie_unidade_medida_w		varchar(30);

BEGIN
if (cd_material_p > 0) then
	begin
	vl_ultima_compra_w 	:= coalesce(obter_dados_ultima_compra(cd_estabelecimento_p, cd_material_p, 'VU'),0);
	if (vl_ultima_compra_w > 0) then
		begin
		cd_unidade_medida_estoque_w	:= obter_dados_material(cd_material_p, 'UME');
		cd_unidade_medida_compra_w	:= obter_dados_material(cd_material_p, 'UMP');
		cd_unidade_medida_consumo_w	:= obter_dados_material(cd_material_p, 'UMC');

		if (cd_unidade_medida_compra_w = cd_unidade_medida_p) then
			ie_unidade_medida_w	:= 'UMP';
		elsif (cd_unidade_medida_estoque_w = cd_unidade_medida_p) then
			ie_unidade_medida_w	:= 'UME';
		elsif (cd_unidade_medida_consumo_w = cd_unidade_medida_p) then
			ie_unidade_medida_w	:= 'UMC';
		else
			ie_unidade_medida_w	:= 'UMP';
		end if;

		vl_ultima_compra_w := sup_obter_valor_convertido(cd_material_p, vl_ultima_compra_w, cd_unidade_medida_estoque_w, ie_unidade_medida_w);
		end;
	end if;
	end;
end if;

vl_retorno_p := vl_ultima_compra_w;
end;
$body$
