CREATE OR REPLACE PROCEDURE odont_alterar_status ( nr_sequencia_p bigint, ie_status_p text, cd_pessoa_fisica_p text, nr_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_historico_w		varchar(255);
ds_atendimento_w	varchar(255);
cd_procedimento_w	bigint;
nr_seq_proc_interno_w	bigint;
ie_origem_proced_w	bigint;
qt_procedimento_w	double precision;
ie_status_w		varchar(80);


BEGIN 
if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') and (cd_pessoa_fisica_p IS NOT NULL AND cd_pessoa_fisica_p::text <> '') then 
 
	select	ie_status 
	into STRICT	ie_status_w 
	from	odont_procedimento 
	where	nr_sequencia = nr_sequencia_p;
	 
	if (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
		ds_atendimento_w := wheb_mensagem_pck.get_texto(307420)||': '||nr_atendimento_p;
	else 
		ds_atendimento_w := '';
	end if;
	 
	select	max(wheb_mensagem_pck.get_texto(798633)||': '|| substr(obter_valor_dominio(5100,ie_status_p),1,255)||chr(10)|| 
		wheb_mensagem_pck.get_texto(798635)||': '||nr_dente||chr(10)|| 
		ds_atendimento_w) 
	into STRICT	ds_historico_w	 
	from	odont_procedimento		 
	where	nr_sequencia = nr_sequencia_p 
	and	(nr_dente IS NOT NULL AND nr_dente::text <> '');
	 
	if (coalesce(ds_historico_w::text, '') = '') then 
		select	max(wheb_mensagem_pck.get_texto(798633)||': '|| substr(obter_valor_dominio(5100,ie_status_p),1,255)||chr(10)|| 
			wheb_mensagem_pck.get_texto(798641)||': '||substr(obter_valor_dominio(5101,ie_tratamento),1,255)||chr(10)|| 
			ds_atendimento_w) 
		into STRICT	ds_historico_w	 
		from	odont_procedimento		 
		where	nr_sequencia = nr_sequencia_p 
		and	(ie_tratamento IS NOT NULL AND ie_tratamento::text <> '');
	end if;
	 
	update	odont_procedimento		 
	set	ie_status = ie_status_p 
	where	nr_sequencia = nr_sequencia_p;
 
	insert into odont_historico(	 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		ds_titulo, 
		ds_historico, 
		nr_atendimento, 
		ie_tipo, 
		dt_historico, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		ie_status, 
		nr_seq_odont_proced, 
		cd_pessoa_fisica) 
	values (	nextval('odont_historico_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		wheb_mensagem_pck.get_texto(798633)||': '||substr(obter_valor_dominio(5100,ie_status_p),1,255), 
		coalesce(ds_historico_w,wheb_mensagem_pck.get_texto(798633)||': '||substr(obter_valor_dominio(5100,ie_status_p),1,255)), 
		nr_atendimento_p, 
		'S', 
		clock_timestamp(), 
		clock_timestamp(), 
		nm_usuario_p, 
		ie_status_p, 
		nr_sequencia_p, 
		cd_pessoa_fisica_p);
 
	if (ie_status_p = 'R') and (nr_atendimento_p IS NOT NULL AND nr_atendimento_p::text <> '') then 
		begin 
		select	max(cd_procedimento), 
			max(nr_seq_proc_interno), 
			max(ie_origem_proced), 
			max(qt_procedimento) 
		into STRICT	cd_procedimento_w, 
			nr_seq_proc_interno_w, 
			ie_origem_proced_w, 
			qt_procedimento_w 
		from	odont_procedimento 
		where	nr_sequencia = nr_sequencia_p;
 
		CALL odonto_inserir_proced_conta(nr_atendimento_p, cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w, qt_procedimento_w, nr_sequencia_p, nm_usuario_p);
		end;
	end if;
	 
	if (ie_status_p = 'C') and (ie_status_w = 'R') then 
		begin 
		select	max(cd_procedimento), 
			max(nr_seq_proc_interno), 
			max(ie_origem_proced), 
			max(qt_procedimento) 
		into STRICT	cd_procedimento_w, 
			nr_seq_proc_interno_w, 
			ie_origem_proced_w, 
			qt_procedimento_w 
		from	odont_procedimento 
		where	nr_sequencia = nr_sequencia_p;
 
		CALL odonto_inserir_proced_conta(nr_atendimento_p, cd_procedimento_w, ie_origem_proced_w, nr_seq_proc_interno_w, (qt_procedimento_w*-1), nr_sequencia_p, nm_usuario_p);
		end;
	end if;
	 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
