SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE odont_desfazer_status_html ( nr_seq_odont_proced_p bigint, ie_status_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_odont_historico_w	bigint;
nr_atendimento_w		bigint;

c01 CURSOR FOR
	SELECT 	nr_seq_proc_interno,
		qt_procedimento
		CD_PROCEDIMENTO,
		IE_ORIGEM_PROCED,
		nr_sequencia
	from 	odont_procedimento_proc
	where 	nr_seq_plano_proc = nr_seq_odont_proced_p;

BEGIN
if (nr_seq_odont_proced_p IS NOT NULL AND nr_seq_odont_proced_p::text <> '') then

		update	odont_procedimento
		set	ie_status = 'P'
		where	nr_sequencia = nr_seq_odont_proced_p;

		select	max(nr_sequencia),
			max(nr_atendimento)
		into STRICT	nr_seq_odont_historico_w,
			nr_atendimento_w
		from	odont_historico
		where	nr_seq_odont_proced = nr_seq_odont_proced_p;

		if (nr_seq_odont_historico_w IS NOT NULL AND nr_seq_odont_historico_w::text <> '') then

			delete	FROM ODONT_HISTORICO_PROCED
			where 	NR_SEQ_ODONT_HIST = nr_seq_odont_historico_w;

			delete	FROM odont_historico
			where	nr_seq_odont_proced = nr_seq_odont_proced_p;

		end if;

		if (ie_status_p = 'R') then			
			for r_C01_w in C01 loop
		
			CALL odonto_inserir_proced_conta(nr_atendimento_w, r_C01_w.CD_PROCEDIMENTO, r_C01_w.IE_ORIGEM_PROCED, r_C01_w.nr_seq_proc_interno, (r_C01_w.CD_PROCEDIMENTO*-1), r_C01_w.nr_sequencia, nm_usuario_p, 'S');
			
			end loop;			
		end if;

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
