SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oec_process_request () AS $body$
DECLARE


request_sts 		varchar(20000);
return_w    		text;
transaction_id_list_w 	varchar(20000);
count_w			smallint;
transaction_id_w 	varchar(2000);

c01 CURSOR FOR
	SELECT 	b.CD_SEQ_TRANSACTION as transaction_id
	from 	AUTORIZACAO_CONVENIO a,
		oec_claim b,
		ESTAGIO_AUTORIZACAO c
	where 	a.nr_sequencia = b.nr_sequencia_autor
	and	a.NR_SEQ_ESTAGIO = c.nr_sequencia
	and 	c.ie_interno = 66    --checking only for the ones which are under processing 
	and 	coalesce(b.CD_STS_PROCESSS_STATUS::text, '') = '';

BEGIN
for r01 in c01 loop
	if (transaction_id_list_w IS NOT NULL AND transaction_id_list_w::text <> '') then
		transaction_id_list_w := transaction_id_list_w || ', "' || r01.transaction_id || '"';
	else
		transaction_id_list_w :=' "' || r01.transaction_id || '"';
	end if;

end loop;
request_sts :=   '{' || chr(10) ||'"transactionIdList": ['|| transaction_id_list_w ||']'
                 || chr(10) || ', "userName":"system"'|| chr(10) || '}';
return_w := BIFROST.SEND_INTEGRATION_CONTENT('eclipse.oec.sts.event', request_sts, 'akshay');

EXCEPTION
	WHEN OTHERS THEN 
	NULL;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
