CREATE OR REPLACE PROCEDURE oft_acoes_pronto_atendimento (nr_atendimento_p bigint, nr_seq_consulta_p bigint, ie_opcao_p text, cd_profissional_p text) AS $body$
DECLARE


dt_alteracao_w	      timestamp;
nr_seq_status_w      oft_consulta.nr_seq_status%type;
cd_estabelecimento_w estabelecimento.cd_estabelecimento%type   := wheb_usuario_pck.get_cd_estabelecimento;
nm_usuario_w         usuario.nm_usuario%type                    := wheb_usuario_pck.get_nm_usuario;


BEGIN
dt_alteracao_w := clock_timestamp();

--Iniciar triagem
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'IT')) then
	update	atendimento_paciente
	set 	   dt_inicio_atendimento   = dt_alteracao_w
	where	   nr_atendimento			   = nr_atendimento_p;

	CALL gerar_atend_escuta(nr_atendimento_p,cd_profissional_p,nm_usuario_w);
end if;

--Desfazer início triagem
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'DT')) then
	update	atendimento_paciente
	set 	   dt_inicio_atendimento    = NULL
	where	   nr_atendimento			   = nr_atendimento_p;
end if;

--Finalizar triagem
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'FT')) then
	update	atendimento_paciente
	set 	   dt_fim_triagem    = dt_alteracao_w
	where	   nr_atendimento		= nr_atendimento_p;
end if;

--Desfazer fim triagem
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'DFT')) then
	update	atendimento_paciente
	set 	   dt_fim_triagem     = NULL
	where	   nr_atendimento		= nr_atendimento_p;
end if;

--Iniciar consulta
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'IC')) then
	update	atendimento_paciente
	set 	   dt_atend_medico   = dt_alteracao_w
	where	   nr_atendimento    = nr_atendimento_p;

   if (coalesce(nr_seq_consulta_p,0) > 0) then
      CALL iniciar_finalizar_consulta(nr_seq_consulta_p,'I',nm_usuario_w,cd_estabelecimento_w);
   end if;
end if;

--Desfazer início consulta
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'DIC')) then
	update	atendimento_paciente
	set 	   dt_atend_medico    = NULL
	where	   nr_atendimento    = nr_atendimento_p;
end if;

--Finalizar consulta
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'FC')) then
	update	atendimento_paciente
	set 	   dt_fim_consulta   = dt_alteracao_w
	where	   nr_atendimento    = nr_atendimento_p;
end if;

--Desfazer fim consulta
if	((coalesce(nr_atendimento_p,0) > 0) and (ie_opcao_p = 'DFC')) then
	update	atendimento_paciente
	set 	   dt_fim_consulta    = NULL
	where	   nr_atendimento    = nr_atendimento_p;

   if (coalesce(nr_seq_consulta_p,0) > 0) then
      select   max(nr_seq_status)
      into STRICT     nr_seq_status_w
      from     oft_consulta
      where    nr_sequencia = nr_seq_consulta_p;

      CALL desfazer_fim_consulta_oft(nr_seq_consulta_p,nr_seq_status_w,cd_estabelecimento_w,nm_usuario_w);
   end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
