CREATE OR REPLACE PROCEDURE oft_gerar_auto_refr_rec_suger ( nr_seq_consulta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_profissional_w	varchar(10);
qt_oft_refracao_w	bigint;
				

BEGIN 
 
select	obter_pessoa_fisica_usuario(nm_usuario_p,'C') 
into STRICT	cd_profissional_w
;
 
select	count(*) 
into STRICT	qt_oft_refracao_w 
from	oft_auto_refracao 
where	nr_seq_consulta = nr_seq_consulta_p 
and 	((ie_refracao_sugerida = 'N') or (coalesce(ie_refracao_sugerida::text, '') = ''));
 
if (qt_oft_refracao_w > 0) then 
	begin 
	insert into oft_auto_refracao( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_consulta, 
		vl_od_ard_esf, 
		vl_od_ard_cil, 
		vl_od_ard_eixo, 
		qt_reflexo_od, 
		qt_distancia_np, 
		vl_oe_ard_esf, 
		vl_oe_ard_cil, 
		vl_oe_ard_eixo, 
		qt_reflexo_oe, 
		vl_od_are_esf, 
		vl_od_are_cil, 
		vl_od_are_eixo, 
		qt_reflexo_are_od, 
		vl_oe_are_esf, 
		vl_oe_are_cil, 
		vl_oe_are_eixo, 
		qt_reflexo_are_oe, 
		vl_od_lenso_esf, 
		vl_od_lenso_cil, 
		vl_od_lenso_eixo, 
		vl_adicao, 
		vl_oe_lenso_esf, 
		vl_oe_lenso_cil, 
		vl_oe_lenso_eixo, 
		cd_profissional, 
		dt_exame, 
		ds_observacao, 
		ie_refracao_sugerida, 
		ie_situacao) 
	SELECT	nextval('oft_auto_refracao_seq'),  
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_consulta_p, 
		CASE WHEN ie_receita_dinamica='S' THEN vl_od_ard_esf  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_od_ard_cil  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_od_ard_eixo  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN qt_reflexo_od  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN qt_distancia_np  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_oe_ard_esf  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_oe_ard_cil  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_oe_ard_eixo  ELSE null END , 
		CASE WHEN ie_receita_dinamica='S' THEN qt_reflexo_oe  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN vl_od_are_esf  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN vl_od_are_cil  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN vl_od_are_eixo  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN qt_reflexo_are_od  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN vl_oe_are_esf  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN vl_oe_are_cil  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN vl_oe_are_eixo  ELSE null END , 
		CASE WHEN ie_receita_estatica='S' THEN qt_reflexo_are_oe  ELSE null END , 
		vl_od_lenso_esf, 
		vl_od_lenso_cil, 
		vl_od_lenso_eixo, 
		vl_adicao, 
		vl_oe_lenso_esf, 
		vl_oe_lenso_cil, 
		vl_oe_lenso_eixo, 
		cd_profissional_w, 
		clock_timestamp(), 
		ds_observacao, 
		'S', 
		'A' 
	from	oft_auto_refracao 
	where	nr_seq_consulta = nr_seq_consulta_p 
	and	ie_situacao = 'A' 
	and 	((ie_refracao_sugerida = 'N') or (coalesce(ie_refracao_sugerida::text, '') = ''));
	commit;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
