SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_gerar_rec_oculos_auto_re (nr_seq_consulta_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_profissional_w	varchar(10);
qt_oft_auto_refracao_w	bigint;
				

BEGIN 
 
select	obter_pessoa_fisica_usuario(nm_usuario_p,'C') 
into STRICT	cd_profissional_w
;
 
select	count(*) 
into STRICT	qt_oft_auto_refracao_w 
from	oft_auto_refracao 
where	nr_seq_consulta = nr_seq_consulta_p;
 
if (qt_oft_auto_refracao_w > 0) then 
	begin 
	insert into oft_oculos( 
		nr_sequencia, 
		dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_seq_consulta, 
		vl_od_pl_dio_esf, 
		vl_od_pl_dio_cil, 
		vl_od_pl_eixo, 
		vl_oe_pl_dio_esf, 
		vl_oe_pl_dio_cil, 
		vl_oe_pl_eixo, 
		cd_profissional, 
		dt_registro, 
		ds_observacao, 
		ie_situacao) 
	SELECT	nextval('oft_oculos_seq'), 
		clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_seq_consulta_p, 
		CASE WHEN ie_receita_dinamica='S' THEN vl_od_ard_esf  ELSE CASE WHEN ie_receita_estatica='S' THEN vl_od_are_esf  ELSE null END  END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_od_ard_cil  ELSE CASE WHEN ie_receita_estatica='S' THEN vl_od_are_cil  ELSE null END  END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_od_ard_eixo  ELSE CASE WHEN ie_receita_estatica='S' THEN vl_od_are_eixo  ELSE null END  END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_oe_ard_esf  ELSE CASE WHEN ie_receita_estatica='S' THEN vl_oe_are_esf  ELSE null END  END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_oe_ard_cil  ELSE CASE WHEN ie_receita_estatica='S' THEN vl_oe_are_cil  ELSE null END  END , 
		CASE WHEN ie_receita_dinamica='S' THEN vl_oe_ard_eixo  ELSE CASE WHEN ie_receita_estatica='S' THEN vl_oe_are_eixo  ELSE null END  END , 
		cd_profissional_w, 
		clock_timestamp(), 
		null, 
		'A' 
	from	oft_auto_refracao 
	where	nr_seq_consulta = nr_seq_consulta_p 
	and		coalesce(ie_situacao, 'A') = 'A';
	commit;
	end;
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
