SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_get_amsler_grid ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_opcao_p text, vListaAmslerGrid INOUT strRecTypeFormOft) AS $body$
DECLARE

  ds_erro_w varchar(4000);
  ds_amsler_od_w grade_amsler.ds_amsler_od%type;
  ds_amsler_oe_w grade_amsler.ds_amsler_oe%type;
  nm_usuario_w usuario.nm_usuario%TYPE := wheb_usuario_pck.get_nm_usuario;
  c_amsler_grid_form CURSOR FOR
    SELECT a.ds_amsler_od,
      a.ds_amsler_oe
    FROM grade_amsler a,
      oft_consulta_formulario b
    WHERE b.nr_sequencia       = a.nr_seq_consulta_form
    AND a.nr_seq_consulta_form = nr_seq_consulta_form_p
    AND ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    OR (a.nm_usuario           = nm_usuario_w))
    AND ((coalesce(a.dt_inativacao::text, '') = '')
    OR (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
    ORDER BY a.nr_sequencia;
  c_amsler_grid_patient CURSOR FOR
    SELECT a.ds_amsler_od,
      a.ds_amsler_oe
    FROM grade_amsler a,
      oft_consulta b
    WHERE a.nr_seq_consulta = b.nr_sequencia
    AND b.cd_pessoa_fisica  = cd_pessoa_fisica_p
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND coalesce(a.dt_inativacao::text, '') = ''
    AND b.nr_sequencia     <> nr_seq_consulta_p
    ORDER BY a.nr_sequencia;
BEGIN
  BEGIN
    IF (coalesce(nr_seq_consulta_p,0) > 0) AND (vListaAmslerGrid.COUNT > 0) THEN
      IF (ie_opcao_p             = 'F') THEN
        FOR r_amsler_grid_form IN c_amsler_grid_form
        LOOP
          BEGIN
            ds_amsler_od_w := r_amsler_grid_form.ds_amsler_od;
            ds_amsler_oe_w := r_amsler_grid_form.ds_amsler_oe;
          END;
        END LOOP;
      ELSE
        FOR r_amsler_grid_patient IN c_amsler_grid_patient
        LOOP
          BEGIN
            ds_amsler_od_w := r_amsler_grid_patient.ds_amsler_od;
            ds_amsler_oe_w := r_amsler_grid_patient.ds_amsler_oe;
          END;
        END LOOP;
      END IF;
      FOR i IN 1..vListaAmslerGrid.COUNT
      LOOP
        BEGIN
          IF (ie_opcao_p = 'F') OR (vListaAmslerGrid[i].ie_obter_resultado = 'S') THEN
            CASE UPPER(vListaAmslerGrid[i].nm_campo)
            WHEN 'DS_AMSLER_OD' THEN
              vListaAmslerGrid[i].ds_valor := ds_amsler_od_w;
            WHEN 'DS_AMSLER_OE' THEN
              vListaAmslerGrid[i].ds_valor := ds_amsler_oe_w;
            ELSE
              NULL;
            END CASE;
          END IF;
        END;
      END LOOP;
    END IF;
  EXCEPTION
  WHEN invalid_cursor_state THEN
    NULL;
  WHEN OTHERS THEN
    ds_erro_w := SUBSTR(sqlerrm,1,4000);
    UPDATE OFT_CONSULTA_FORMULARIO
    SET ds_stack = SUBSTR(dbms_utility.format_call_stack
      ||ds_erro_w,1,4000)
    WHERE nr_sequencia = nr_seq_consulta_form_p;
    raise;
  END;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_get_amsler_grid ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_opcao_p text, vListaAmslerGrid INOUT strRecTypeFormOft) FROM PUBLIC;

