SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_get_patient_complaints ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_opcao_p text, vListaPatComplaints INOUT strRecTypeFormOft) AS $body$
DECLARE

  ds_erro_w varchar(4000);
  ds_reason_consultation_w atend_queixa_paciente.ds_motivo_consulta%type;
  ds_current_illness_w atend_queixa_paciente.ds_doenca_atual%type;
  nm_usuario_w usuario.nm_usuario%TYPE := wheb_usuario_pck.get_nm_usuario;
  c_pat_complaints_form CURSOR FOR
    SELECT a.ds_motivo_consulta,
      a.ds_doenca_atual
    FROM atend_queixa_paciente a,
      oft_consulta_formulario b
    WHERE b.nr_sequencia       = a.nr_seq_consulta_form
    AND a.nr_seq_consulta_form = nr_seq_consulta_form_p
    AND ((a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    OR (a.nm_usuario           = nm_usuario_w))
    AND ((coalesce(a.dt_inativacao::text, '') = '')
    OR (b.dt_inativacao IS NOT NULL AND b.dt_inativacao::text <> ''))
    ORDER BY a.nr_sequencia;
  c_pat_complaints_patient CURSOR FOR
    SELECT a.ds_motivo_consulta,
      a.ds_doenca_atual
    FROM atend_queixa_paciente a,
      oft_consulta b
    WHERE a.nr_atendimento = nr_atendimento_p
    AND b.nr_atendimento   = a.nr_atendimento
    AND b.cd_pessoa_fisica = cd_pessoa_fisica_p
    AND (a.dt_liberacao IS NOT NULL AND a.dt_liberacao::text <> '')
    AND coalesce(a.dt_inativacao::text, '') = ''
    ORDER BY a.nr_sequencia;
BEGIN
  BEGIN
    IF (coalesce(nr_seq_consulta_p,0) > 0) AND (vListaPatComplaints.COUNT > 0) THEN
      IF (ie_opcao_p             = 'F') THEN
        FOR r_pat_complaints_form IN c_pat_complaints_form
        LOOP
          BEGIN
            ds_reason_consultation_w := r_pat_complaints_form.ds_motivo_consulta;
            ds_current_illness_w     := r_pat_complaints_form.ds_doenca_atual;
          END;
        END LOOP;
      ELSE
        FOR r_pat_complaints_patient IN c_pat_complaints_patient
        LOOP
          BEGIN
            ds_reason_consultation_w := r_pat_complaints_patient.ds_motivo_consulta;
            ds_current_illness_w     := r_pat_complaints_patient.ds_doenca_atual;
          END;
        END LOOP;
      END IF;
      FOR i IN 1..vListaPatComplaints.COUNT
      LOOP
        BEGIN
          CASE UPPER(vListaPatComplaints[i].nm_campo)
          WHEN 'DS_MOTIVO_CONSULTA' THEN
            vListaPatComplaints[i].ds_valor := ds_reason_consultation_w;
          WHEN 'DS_DOENCA_ATUAL' THEN
            vListaPatComplaints[i].ds_valor := ds_current_illness_w;
          ELSE
            NULL;
          END CASE;
        END;
      END LOOP;
    END IF;
  EXCEPTION
  WHEN OTHERS THEN
    ds_erro_w := SUBSTR(sqlerrm,1,4000);
    UPDATE OFT_CONSULTA_FORMULARIO
    SET ds_stack = SUBSTR(dbms_utility.format_call_stack
      ||ds_erro_w,1,4000)
    WHERE nr_sequencia = nr_seq_consulta_form_p;
  END;
END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_get_patient_complaints ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, cd_pessoa_fisica_p text, nr_atendimento_p bigint, ie_opcao_p text, vListaPatComplaints INOUT strRecTypeFormOft) FROM PUBLIC;

