SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_biomicroscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaBiomicroscopia strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w						oft_biomicroscopia.nr_sequencia%type;
ds_observacao_w					oft_biomicroscopia.ds_observacao%type;
dt_exame_w							oft_biomicroscopia.dt_exame%type;
ds_biomicroscopia_od_w			oft_biomicroscopia.ds_biomicroscopia_od%type;
ds_biomicroscopia_pos_oe_w		oft_biomicroscopia.ds_biomicroscopia_pos_oe%type;
ds_biomicroscopia_oe_w			oft_biomicroscopia.ds_biomicroscopia_oe%type;
ie_normal_oe_w						oft_biomicroscopia.ie_normal_oe%type;
ds_biomicroscopia_pos_od_w		oft_biomicroscopia.ds_biomicroscopia_pos_od%type;
ie_normal_od_w						oft_biomicroscopia.ie_normal_od%type;
ie_segmento_w						oft_biomicroscopia.ie_segmento%type;
ds_mapeamento_od_w				oft_biomicroscopia.ds_mapeamento_od%type;
ds_mapeamento_oe_w				oft_biomicroscopia.ds_mapeamento_oe%type;
cd_profissional_w					oft_biomicroscopia.cd_profissional%type;
nm_usuario_w						usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w					varchar(1) := 'N';
ds_erro_w							varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaBiomicroscopia.count > 0) then
	for i in 1..vListaBiomicroscopia.count loop
		begin
		if (vListaBiomicroscopia[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaBiomicroscopia[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaBiomicroscopia[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w					:= vListaBiomicroscopia[i].ds_valor;
				when 'DT_EXAME' then
					dt_exame_w 							:= pkg_date_utils.get_DateTime(vListaBiomicroscopia[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w 					:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_BIOMICROSCOPIA_OD' then
					ds_biomicroscopia_od_w 			:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_BIOMICROSCOPIA_OE' then
					ds_biomicroscopia_oe_w 			:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_BIOMICROSCOPIA_POS_OD' then
					ds_biomicroscopia_pos_od_w 	:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_BIOMICROSCOPIA_POS_OE' then
					ds_biomicroscopia_pos_oe_w 	:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_MAPEAMENTO_OD' then
					ds_mapeamento_od_w 				:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_MAPEAMENTO_OE' then
					ds_mapeamento_oe_w 				:= vListaBiomicroscopia[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'IE_NORMAL_OD' then
					ie_normal_od_w 					:= vListaBiomicroscopia[i].ds_valor;
					if (ie_normal_od_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;
				when 'IE_NORMAL_OE' then
					ie_normal_oe_w 					:= vListaBiomicroscopia[i].ds_valor;
					if (ie_normal_oe_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;
				when 'IE_SEGMENTO' then
					ie_segmento_w 						:= vListaBiomicroscopia[i].ds_valor;
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_biomicroscopia
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_biomicroscopia
		set		dt_atualizacao					=	clock_timestamp(),
					nm_usuario						=  nm_usuario_w,
					cd_profissional				= coalesce(cd_profissional_w,cd_profissional),
					dt_exame							=	coalesce(dt_exame_w,dt_exame),
					ds_biomicroscopia_oe			=	ds_biomicroscopia_oe_w,
					ds_biomicroscopia_od			=	ds_biomicroscopia_od_w,
					ds_observacao					=	ds_observacao_w,
					ie_segmento						=	ie_segmento_w,
					ds_biomicroscopia_pos_oe	=	ds_biomicroscopia_pos_oe_w,
					ds_biomicroscopia_pos_od	=	ds_biomicroscopia_pos_od_w,
					ds_mapeamento_od				=	ds_mapeamento_od_w,
					ds_mapeamento_oe				=	ds_mapeamento_oe_w,
					ie_normal_od					=	coalesce(ie_normal_od_w,ie_normal_od),
					ie_normal_oe					=	coalesce(ie_normal_oe_w,ie_normal_oe)
		where		nr_sequencia					=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_biomicroscopia_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_biomicroscopia(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																cd_profissional,
																dt_exame,
																nr_seq_consulta,
																ds_biomicroscopia_oe,
																ds_biomicroscopia_od,
																ds_observacao,
																ie_situacao,
																ie_segmento,
																ds_biomicroscopia_pos_oe,
																ds_biomicroscopia_pos_od,
																ds_mapeamento_od,
																ds_mapeamento_oe,
																ie_normal_od,
																ie_normal_oe,
																nr_seq_consulta_form)
			values (		nr_sequencia_w,
																clock_timestamp(),
																nm_usuario_w,
																clock_timestamp(),
																nm_usuario_w,
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
																coalesce(dt_exame_w,clock_timestamp()),
																nr_seq_consulta_p,
																ds_biomicroscopia_oe_w,
																ds_biomicroscopia_od_w,
																ds_observacao_w,
																'A',
																ie_segmento_w,
																ds_biomicroscopia_pos_oe_w,
																ds_biomicroscopia_pos_od_w,
																ds_mapeamento_od_w,
																ds_mapeamento_oe_w,
																coalesce(ie_normal_od_w,'S'),
																coalesce(ie_normal_oe_w,'S'),
																nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_consulta_imagem(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																ds_arquivo_backup,
																nr_seq_biomicroscopia)
																SELECT	nextval('oft_consulta_imagem_seq'),
																			clock_timestamp(),
																			nm_usuario_w,
																			clock_timestamp(),
																			nm_usuario_w,
																			ds_titulo,
																			ds_arquivo,
																			ds_arquivo_backup,
																			nr_sequencia_w
																from		oft_consulta_imagem a,
																			oft_biomicroscopia b
																where		a.nr_seq_biomicroscopia = 	b.nr_sequencia
																and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
																and		b.nr_seq_consulta			=	nr_seq_consulta_p;

				insert	into oft_imagem_exame(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta,
																ie_lado,
																nr_seq_biomicroscopia,
																ie_situacao,
																cd_profissional)
													SELECT	nextval('oft_imagem_exame_seq'),
																clock_timestamp(),
																nm_usuario_w,
																clock_timestamp(),
																nm_usuario_w,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta_p,
																ie_lado,
																nr_sequencia_w,
																'A',
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C'))
													from		oft_imagem_exame a,
																oft_biomicroscopia b
													where		a.nr_seq_biomicroscopia = 	b.nr_sequencia
													and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
													and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_biomicroscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaBiomicroscopia strRecTypeFormOft) FROM PUBLIC;

