SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_ceratometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaCeratometria strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w						oft_cerastocopia.nr_sequencia%type;
dt_exame_w							oft_cerastocopia.dt_registro%type;
ds_comentario_w					oft_cerastocopia.ds_comentario%type;
vl_od_eixo_ceratometria_w		oft_cerastocopia.vl_od_eixo_ceratometria%type;
vl_od_eixo_ceratometria_k2_w	oft_cerastocopia.vl_od_eixo_ceratometria_k2%type;
vl_od_ma_cu_ceratometria_w		oft_cerastocopia.vl_od_ma_cu_ceratometria%type;
vl_od_me_cu_ceratometria_w		oft_cerastocopia.vl_od_me_cu_ceratometria%type;
vl_oe_eixo_ceratometria_w		oft_cerastocopia.vl_oe_eixo_ceratometria%type;
vl_oe_eixo_ceratometria_k2_w	oft_cerastocopia.vl_oe_eixo_ceratometria_k2%type;
vl_oe_ma_cu_ceratometria_w		oft_cerastocopia.vl_oe_ma_cu_ceratometria%type;
vl_oe_me_cu_ceratometria_w		oft_cerastocopia.vl_oe_me_cu_ceratometria%type;
cd_profissional_w					oft_cerastocopia.cd_profissional%type;
nm_usuario_w						usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w					varchar(1) := 'N';
ds_erro_w							varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaCeratometria.count > 0) then
	for i in 1..vListaCeratometria.count loop
		begin
		if (vListaCeratometria[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaCeratometria[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaCeratometria[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w							:= vListaCeratometria[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 									:= pkg_date_utils.get_DateTime(vListaCeratometria[i].ds_valor);
				when 'DS_COMENTARIO' then
					ds_comentario_w							:= vListaCeratometria[i].ds_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_EIXO_CERATOMETRIA' then
					vl_od_eixo_ceratometria_w				:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_EIXO_CERATOMETRIA_K2' then
					vl_od_eixo_ceratometria_k2_w			:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_MA_CU_CERATOMETRIA' then
					vl_od_ma_cu_ceratometria_w				:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_ME_CU_CERATOMETRIA' then
					vl_od_me_cu_ceratometria_w				:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_EIXO_CERATOMETRIA' then
					vl_oe_eixo_ceratometria_w				:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_EIXO_CERATOMETRIA_K2' then
					vl_oe_eixo_ceratometria_k2_w			:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_MA_CU_CERATOMETRIA' then
					vl_oe_ma_cu_ceratometria_w				:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_ME_CU_CERATOMETRIA' then
					vl_oe_me_cu_ceratometria_w				:= vListaCeratometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_cerastocopia
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_cerastocopia
		set		dt_atualizacao						=	clock_timestamp(),
					nm_usuario							=	nm_usuario_w,
					cd_profissional					= coalesce(cd_profissional_w,cd_profissional),
					dt_registro							=	coalesce(dt_exame_w,dt_registro),
					ds_comentario						=	ds_comentario_w,
					vl_od_eixo_ceratometria			=	vl_od_eixo_ceratometria_w,
					vl_od_eixo_ceratometria_k2		=	vl_od_eixo_ceratometria_k2_w,
					vl_od_ma_cu_ceratometria		=	vl_od_ma_cu_ceratometria_w,
					vl_od_me_cu_ceratometria		=	vl_od_me_cu_ceratometria_w,
					vl_oe_eixo_ceratometria			=	vl_oe_eixo_ceratometria_w,
					vl_oe_eixo_ceratometria_k2		=	vl_oe_eixo_ceratometria_k2_w,
					vl_oe_ma_cu_ceratometria		=	vl_oe_ma_cu_ceratometria_w,
					vl_oe_me_cu_ceratometria		=	vl_oe_me_cu_ceratometria_w
		where		nr_sequencia						=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_cerastocopia_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_cerastocopia(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_registro,
														nr_seq_consulta,
														ie_situacao,
														ds_comentario,
														vl_od_eixo_ceratometria,
														vl_od_eixo_ceratometria_k2,
														vl_od_ma_cu_ceratometria,
														vl_od_me_cu_ceratometria,
														vl_oe_eixo_ceratometria,
														vl_oe_eixo_ceratometria_k2,
														vl_oe_ma_cu_ceratometria,
														vl_oe_me_cu_ceratometria,
														nr_seq_consulta_form)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_comentario_w,
														vl_od_eixo_ceratometria_w,
														vl_od_eixo_ceratometria_k2_w,
														vl_od_ma_cu_ceratometria_w,
														vl_od_me_cu_ceratometria_w,
														vl_oe_eixo_ceratometria_w,
														vl_oe_eixo_ceratometria_k2_w,
														vl_oe_ma_cu_ceratometria_w,
														vl_oe_me_cu_ceratometria_w,
														nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_imagem_exame(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta,
																ie_lado,
																nr_seq_cerastocopia,
																ie_situacao,
																cd_profissional)
													SELECT	nextval('oft_imagem_exame_seq'),
																clock_timestamp(),
																nm_usuario_w,
																clock_timestamp(),
																nm_usuario_w,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta_p,
																ie_lado,
																nr_sequencia_w,
																'A',
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C'))
													from		oft_imagem_exame a,
																oft_cerastocopia b
													where		a.nr_seq_cerastocopia	= 	b.nr_sequencia
													and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
													and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_ceratometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaCeratometria strRecTypeFormOft) FROM PUBLIC;

