SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_daltonismo ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaDaltonismo strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w				oft_daltonismo.nr_sequencia%type;
dt_exame_w					oft_daltonismo.dt_registro%type;
ds_observacao_w			oft_daltonismo.ds_observacao%type;
vl_od_w						oft_daltonismo.vl_od%type;
vl_oe_w						oft_daltonismo.vl_oe%type;
ie_resultado_w				oft_daltonismo.ie_resultado%type;
cd_profissional_w			oft_daltonismo.cd_profissional%type;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w			varchar(1) := 'N';
ds_erro_w					varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaDaltonismo.count > 0) then
	for i in 1..vListaDaltonismo.count loop
		begin
		if (vListaDaltonismo[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaDaltonismo[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaDaltonismo[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w					:= vListaDaltonismo[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 							:= pkg_date_utils.get_DateTime(vListaDaltonismo[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w					:= vListaDaltonismo[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'VL_OD' then
					vl_od_w								:= vListaDaltonismo[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'VL_OE' then
					vl_oe_w								:= vListaDaltonismo[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'IE_RESULTADO' then
					ie_resultado_w						:= vListaDaltonismo[i].ds_valor;
					ie_registrado_w					:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_daltonismo
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_daltonismo
		set		dt_atualizacao		=	clock_timestamp(),
					nm_usuario			=	nm_usuario_w,
					cd_profissional	= coalesce(cd_profissional_w,cd_profissional),
					dt_registro			=	coalesce(dt_exame_w,dt_registro),
					ds_observacao		=	ds_observacao_w,
					vl_od					=	vl_od_w,
					vl_oe					=	vl_oe_w,
					ie_resultado		=	ie_resultado_w
		where		nr_sequencia		=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_daltonismo_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_daltonismo(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_registro,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														vl_od,
														vl_oe,
														ie_resultado,
														nr_seq_consulta_form)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														vl_od_w,
														vl_oe_w,
														ie_resultado_w,
														nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_consulta_imagem(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																ds_arquivo_backup,
																nr_seq_daltonismo)
																SELECT	nextval('oft_consulta_imagem_seq'),
																			clock_timestamp(),
																			nm_usuario_w,
																			clock_timestamp(),
																			nm_usuario_w,
																			ds_titulo,
																			ds_arquivo,
																			ds_arquivo_backup,
																			nr_sequencia_w
																from		oft_consulta_imagem a,
																			oft_daltonismo b
																where		a.nr_seq_daltonismo 		= 	b.nr_sequencia
																and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
																and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_daltonismo ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaDaltonismo strRecTypeFormOft) FROM PUBLIC;

