SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_dnp ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, vListaDnp strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w				oft_dnp.nr_sequencia%type;
dt_exame_w					oft_dnp.dt_exame%type;
ds_observacao_w			oft_dnp.ds_observacao%type;
vl_od_ard_dnp_w			oft_dnp.vl_od_ard_dnp%type;
vl_ard_dp_w					oft_dnp.vl_ard_dp%type;
vl_oe_ard_dnp_w			oft_dnp.vl_oe_ard_dnp%type;
vl_od_pp_ard_dnp_w		oft_dnp.vl_od_pp_ard_dnp%type;
vl_pp_ard_dp_w				oft_dnp.vl_pp_ard_dp%type;
vl_oe_pp_ard_dnp_w		oft_dnp.vl_oe_pp_ard_dnp%type;
cd_profissional_w			oft_dnp.cd_profissional%type;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w			varchar(1) := 'N';
ds_erro_w					varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaDnp.count > 0) then
	for i in 1..vListaDnp.count loop
		begin
		if (vListaDnp[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaDnp[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaDnp[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w 	:= vListaDnp[i].ds_valor;
				when 'DT_EXAME' then
					dt_exame_w 							:= pkg_date_utils.get_DateTime(vListaDnp[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w					:= vListaDnp[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'VL_OD_ARD_DNP' then
					vl_od_ard_dnp_w					:= vListaDnp[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'VL_ARD_DP' then
					vl_ard_dp_w							:= vListaDnp[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'VL_OE_ARD_DNP' then
					vl_oe_ard_dnp_w					:= vListaDnp[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'VL_OD_PP_ARD_DNP' then
					vl_od_pp_ard_dnp_w				:= vListaDnp[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'VL_PP_ARD_DP' then
					vl_pp_ard_dp_w						:= vListaDnp[i].nr_valor;
					ie_registrado_w					:= 'S';
				when 'VL_OE_PP_ARD_DNP' then
					vl_oe_pp_ard_dnp_w				:= vListaDnp[i].nr_valor;
					ie_registrado_w					:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_dnp
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_dnp
		set		dt_atualizacao		=	clock_timestamp(),
					nm_usuario			=	nm_usuario_w,
					dt_exame				=	coalesce(dt_exame_w,dt_exame),
					ds_observacao		=	ds_observacao_w,
					vl_od_ard_dnp		=	vl_od_ard_dnp_w,
					vl_ard_dp			=	vl_ard_dp_w,
					vl_oe_ard_dnp		=	vl_oe_ard_dnp_w,
					vl_od_pp_ard_dnp	=	vl_od_pp_ard_dnp_w,
					vl_pp_ard_dp		=	vl_pp_ard_dp_w,
					vl_oe_pp_ard_dnp	=	vl_oe_pp_ard_dnp_w,
					cd_profissional	= coalesce(cd_profissional_w,cd_profissional)
		where		nr_sequencia		=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_dnp_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_dnp(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_exame,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														vl_od_ard_dnp,
														vl_ard_dp,
														vl_oe_ard_dnp,
														vl_od_pp_ard_dnp,
														vl_pp_ard_dp,
														vl_oe_pp_ard_dnp,
														nr_seq_consulta_form)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														vl_od_ard_dnp_w,
														vl_ard_dp_w,
														vl_oe_ard_dnp_w,
														vl_od_pp_ard_dnp_w,
														vl_pp_ard_dp_w,
														vl_oe_pp_ard_dnp_w,
														nr_seq_consulta_form_p);
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_dnp ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, vListaDnp strRecTypeFormOft) FROM PUBLIC;

