SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_exame_externo ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaExameExterno strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w				oft_exame_externo.nr_sequencia%type;
dt_exame_w					oft_exame_externo.dt_exame%type;
ds_observacao_w			oft_exame_externo.ds_observacao%type;
ds_exame_exter_oe_w		oft_exame_externo.ds_exame_exter_oe%type;
ds_exame_exter_od_w		oft_exame_externo.ds_exame_exter_od%type;
ds_exame_externo_face_w	oft_exame_externo.ds_exame_externo_face%type;
ie_normal_od_w				oft_exame_externo.ie_normal_od%type;
ie_normal_oe_w				oft_exame_externo.ie_normal_oe%type;
ie_normal_face_w			oft_exame_externo.ie_normal_face%type;
cd_profissional_w			oft_exame_externo.cd_profissional%type;
nm_usuario_w				usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w			varchar(1) := 'N';
ds_erro_w					varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaExameExterno.count > 0) then
	for i in 1..vListaExameExterno.count loop
		begin
		if (vListaExameExterno[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaExameExterno[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaExameExterno[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w 					:= vListaExameExterno[i].ds_valor;
				when 'DT_EXAME' then
					dt_exame_w 								:= pkg_date_utils.get_DateTime(vListaExameExterno[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w						:= vListaExameExterno[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'DS_EXAME_EXTER_OE' then
					ds_exame_exter_oe_w					:= vListaExameExterno[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'DS_EXAME_EXTER_OD' then
					ds_exame_exter_od_w					:= vListaExameExterno[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'DS_EXAME_EXTERNO_FACE' then
					ds_exame_externo_face_w				:= vListaExameExterno[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_NORMAL_OD' then
					ie_normal_od_w							:= vListaExameExterno[i].ds_valor;
					if (ie_normal_od_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;
				when 'IE_NORMAL_OE' then
					ie_normal_oe_w							:= vListaExameExterno[i].ds_valor;
					if (ie_normal_oe_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;
				when 'IE_NORMAL_FACE' then
					ie_normal_face_w						:= vListaExameExterno[i].ds_valor;
					if (ie_normal_face_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_exame_externo
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_exame_externo
		set		dt_atualizacao				=	clock_timestamp(),
					nm_usuario					=	nm_usuario_w,
					dt_exame						=	coalesce(dt_exame_w,dt_exame),
					ds_observacao				=	ds_observacao_w,
					ds_exame_exter_oe			=	ds_exame_exter_oe_w,
					ds_exame_exter_od			=	ds_exame_exter_od_w,
					ds_exame_externo_face	=	ds_exame_externo_face_w,
					ie_normal_od				=	coalesce(ie_normal_od_w,ie_normal_od),
					ie_normal_oe				=	coalesce(ie_normal_oe_w,ie_normal_oe),
					ie_normal_face				=	coalesce(ie_normal_face_w,ie_normal_face),
					cd_profissional			= coalesce(cd_profissional_w,cd_profissional)
		where		nr_sequencia				=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_exame_externo_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_exame_externo(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_exame,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														ds_exame_exter_oe,
														ds_exame_exter_od,
														ds_exame_externo_face,
														ie_normal_od,
														ie_normal_oe,
														ie_normal_face,
														nr_seq_consulta_form)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														ds_exame_exter_oe_w,
														ds_exame_exter_od_w,
														ds_exame_externo_face_w,
														coalesce(ie_normal_od_w,'S'),
														coalesce(ie_normal_oe_w,'S'),
														coalesce(ie_normal_face_w,'S'),
														nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_consulta_imagem(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																ds_arquivo_backup,
																nr_seq_exame_externo)
																SELECT	nextval('oft_consulta_imagem_seq'),
																			clock_timestamp(),
																			nm_usuario_w,
																			clock_timestamp(),
																			nm_usuario_w,
																			ds_titulo,
																			ds_arquivo,
																			ds_arquivo_backup,
																			nr_sequencia_w
																from		oft_consulta_imagem a,
																			oft_exame_externo b
																where		a.nr_seq_exame_externo = 	b.nr_sequencia
																and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
																and		b.nr_seq_consulta			=	nr_seq_consulta_p;

				insert	into oft_imagem_exame(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta,
																ie_lado,
																nr_seq_oft_exame_externo,
																ie_situacao,
																cd_profissional)
													SELECT	nextval('oft_imagem_exame_seq'),
																clock_timestamp(),
																nm_usuario_w,
																clock_timestamp(),
																nm_usuario_w,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta_p,
																ie_lado,
																nr_sequencia_w,
																'A',
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C'))
													from		oft_imagem_exame a,
																oft_exame_externo b
													where		a.nr_seq_oft_exame_externo = 	b.nr_sequencia
													and		b.nr_seq_consulta_form		= 	nr_seq_consulta_form_ant_p
													and		b.nr_seq_consulta				=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_exame_externo ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaExameExterno strRecTypeFormOft) FROM PUBLIC;

