SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_gonioscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaGonioscopia strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w		oft_gonioscopia.nr_sequencia%type;
dt_exame_w			oft_gonioscopia.dt_registro%type;
ds_observacao_w	oft_gonioscopia.ds_observacao%type;
ie_od_goni_sup_w 	oft_gonioscopia.ie_od_goni_sup%type;
ie_od_goni_esq_w	oft_gonioscopia.ie_od_goni_esq%type;
ie_od_goni_inf_w	oft_gonioscopia.ie_od_goni_inf%type;
ie_od_goni_dir_w	oft_gonioscopia.ie_od_goni_dir%type;
ie_oe_goni_sup_w	oft_gonioscopia.ie_oe_goni_sup%type;
ie_oe_goni_esq_w	oft_gonioscopia.ie_oe_goni_esq%type;
ie_oe_goni_inf_w	oft_gonioscopia.ie_oe_goni_inf%type;
ie_oe_goni_dir_w	oft_gonioscopia.ie_oe_goni_dir%type;
cd_profissional_w	oft_gonioscopia.cd_profissional%type;
nm_usuario_w		usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w	varchar(1) := 'N';
ds_erro_w			varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaGonioscopia.count > 0) then
	for i in 1..vListaGonioscopia.count loop
		begin
		if (vListaGonioscopia[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaGonioscopia[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaGonioscopia[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w						:= vListaGonioscopia[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 								:= pkg_date_utils.get_DateTime(vListaGonioscopia[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OD_GONI_SUP' then
					ie_od_goni_sup_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OD_GONI_ESQ' then
					ie_od_goni_esq_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OD_GONI_INF' then
					ie_od_goni_inf_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OD_GONI_DIR' then
					ie_od_goni_dir_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OE_GONI_SUP' then
					ie_oe_goni_sup_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OE_GONI_ESQ' then
					ie_oe_goni_esq_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OE_GONI_INF' then
					ie_oe_goni_inf_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OE_GONI_DIR' then
					ie_oe_goni_dir_w						:= vListaGonioscopia[i].ds_valor;
					ie_registrado_w						:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_gonioscopia
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_gonioscopia
		set		dt_atualizacao		=	clock_timestamp(),
					nm_usuario			=	nm_usuario_w,
					cd_profissional	= coalesce(cd_profissional_w,cd_profissional),
					dt_registro			=	coalesce(dt_exame_w,dt_registro),
					ds_observacao		=	ds_observacao_w,
					ie_od_goni_sup		=	ie_od_goni_sup_w,
					ie_od_goni_esq		=	ie_od_goni_esq_w,
					ie_od_goni_inf		=	ie_od_goni_inf_w,
					ie_od_goni_dir		=	ie_od_goni_dir_w,
					ie_oe_goni_sup		=	ie_oe_goni_sup_w,
					ie_oe_goni_esq		=	ie_oe_goni_esq_w,
					ie_oe_goni_inf		=	ie_oe_goni_inf_w,
					ie_oe_goni_dir		=	ie_oe_goni_dir_w
		where		nr_sequencia		=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_gonioscopia_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_gonioscopia(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_registro,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														ie_od_goni_sup,
														ie_od_goni_esq,
														ie_od_goni_inf,
														ie_od_goni_dir,
														ie_oe_goni_sup,
														ie_oe_goni_esq,
														ie_oe_goni_inf,
														ie_oe_goni_dir,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														ie_od_goni_sup_w,
														ie_od_goni_esq_w,
														ie_od_goni_inf_w,
														ie_od_goni_dir_w,
														ie_oe_goni_sup_w,
														ie_oe_goni_esq_w,
														ie_oe_goni_inf_w,
														ie_oe_goni_dir_w,
														nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_consulta_imagem(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																ds_arquivo_backup,
																nr_seq_gonioscopia)
																SELECT	nextval('oft_consulta_imagem_seq'),
																			clock_timestamp(),
																			nm_usuario_w,
																			clock_timestamp(),
																			nm_usuario_w,
																			ds_titulo,
																			ds_arquivo,
																			ds_arquivo_backup,
																			nr_sequencia_w
																from		oft_consulta_imagem a,
																			oft_gonioscopia b
																where		a.nr_seq_gonioscopia		= 	b.nr_sequencia
																and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
																and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_gonioscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaGonioscopia strRecTypeFormOft) FROM PUBLIC;

