CREATE OR REPLACE PROCEDURE oft_insere_iridectomia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaIridectomia strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w			oft_iridectomia.nr_sequencia%type;
dt_exame_w				oft_iridectomia.dt_registro%type;
ds_observacao_w		oft_iridectomia.ds_observacao%type;
ie_olho_w				oft_iridectomia.ie_olho%type;
ie_quadrante_w			oft_iridectomia.ie_quadrante%type;
qt_disparos_w 			oft_iridectomia.qt_disparos%type;
qt_disparo_sup_w		oft_iridectomia.qt_disparo_sup%type;
qt_disparo_inf_w		oft_iridectomia.qt_disparo_inf%type;
qt_disparo_tem_w		oft_iridectomia.qt_disparo_tem%type;
qt_disparo_nas_w		oft_iridectomia.qt_disparo_nas%type;
qt_potencia_sup_w		oft_iridectomia.qt_potencia_sup%type;
qt_potencia_inf_w		oft_iridectomia.qt_potencia_inf%type;
qt_potencia_tem_w		oft_iridectomia.qt_potencia_tem%type;
qt_potencia_nas_w		oft_iridectomia.qt_potencia_nas%type;
qt_energia_utilizada_w	oft_iridectomia.qt_energia_utilizada%type;
cd_profissional_w		oft_iridectomia.cd_profissional%type;
nm_usuario_w		usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w		varchar(1) := 'N';
ds_erro_w				varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaIridectomia.count > 0) then
	for i in 1..vListaIridectomia.count loop
		begin
		if (vListaIridectomia[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaIridectomia[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaIridectomia[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w						:= vListaIridectomia[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 								:= pkg_date_utils.get_DateTime(vListaIridectomia[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w						:= vListaIridectomia[i].ds_valor;
					ie_registrado_w						:= 'S';
				when 'IE_OLHO' then
					ie_olho_w								:= vListaIridectomia[i].ds_valor;
				when 'IE_QUADRANTE' then
					ie_quadrante_w							:= vListaIridectomia[i].ds_valor;
				when 'QT_DISPAROS' then
					qt_disparos_w							:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_DISPARO_SUP' then
					qt_disparo_sup_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_DISPARO_INF' then
					qt_disparo_inf_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_DISPARO_TEM' then
					qt_disparo_tem_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_DISPARO_NAS' then
					qt_disparo_nas_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_POTENCIA_SUP' then
					qt_potencia_sup_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_POTENCIA_INF' then
					qt_potencia_inf_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_POTENCIA_TEM' then
					qt_potencia_tem_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_POTENCIA_NAS' then
					qt_potencia_nas_w						:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				when 'QT_ENERGIA_UTILIZADA' then
					qt_energia_utilizada_w				:= vListaIridectomia[i].nr_valor;
					ie_registrado_w						:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_iridectomia
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_iridectomia
		set		dt_atualizacao			=	clock_timestamp(),
					nm_usuario				=	nm_usuario_w,
					cd_profissional		= coalesce(cd_profissional_w,cd_profissional),
					dt_registro				=	coalesce(dt_exame_w,dt_registro),
					ds_observacao			=	ds_observacao_w,
					ie_olho					=	ie_olho_w,
					ie_quadrante			=	ie_quadrante_w,
					qt_disparos				=	qt_disparos_w,
					qt_disparo_sup			=	qt_disparo_sup_w,
					qt_disparo_inf			=	qt_disparo_inf_w,
					qt_disparo_tem			=	qt_disparo_tem_w,
					qt_disparo_nas			=	qt_disparo_nas_w,
					qt_potencia_sup		=	qt_potencia_sup_w,
					qt_potencia_inf		=	qt_potencia_inf_w,
					qt_potencia_tem		=	qt_potencia_tem_w,
					qt_potencia_nas		=	qt_potencia_nas_w,
					qt_energia_utilizada	=	qt_energia_utilizada_w
		where		nr_sequencia			=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_iridectomia_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_iridectomia(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_registro,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														ie_olho,
														ie_quadrante,
														qt_disparos,
														qt_disparo_sup,
														qt_disparo_inf,
														qt_disparo_tem,
														qt_disparo_nas,
														qt_potencia_sup,
														qt_potencia_inf,
														qt_potencia_tem,
														qt_potencia_nas,
														qt_energia_utilizada,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														ie_olho_w,
														ie_quadrante_w,
														qt_disparos_w,
														qt_disparo_sup_w,
														qt_disparo_inf_w,
														qt_disparo_tem_w,
														qt_disparo_nas_w,
														qt_potencia_sup_w,
														qt_potencia_inf_w,
														qt_potencia_tem_w,
														qt_potencia_nas_w,
														qt_energia_utilizada_w,
														nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_consulta_imagem(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																ds_arquivo_backup,
																nr_seq_iridectomia)
																SELECT	nextval('oft_consulta_imagem_seq'),
																			clock_timestamp(),
																			nm_usuario_w,
																			clock_timestamp(),
																			nm_usuario_w,
																			ds_titulo,
																			ds_arquivo,
																			ds_arquivo_backup,
																			nr_sequencia_w
																from		oft_consulta_imagem a,
																			oft_iridectomia b
																where		a.nr_seq_iridectomia		= 	b.nr_sequencia
																and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
																and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
