SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_mapeamento_retina ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaMapeamentoRetina strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w					oft_mapeamento_retina.nr_sequencia%type;
dt_exame_w						oft_mapeamento_retina.dt_registro%type;
ds_observacao_w				oft_mapeamento_retina.ds_observacao%type;
ie_normal_od_w					oft_mapeamento_retina.ie_normal_od%type;
ie_normal_oe_w					oft_mapeamento_retina.ie_normal_oe%type;
ds_vitreo_od_w					oft_mapeamento_retina.ds_vitreo_od%type;
ds_vitreo_oe_w					oft_mapeamento_retina.ds_vitreo_oe%type;	
ds_papila_od_w					oft_mapeamento_retina.ds_papila_od%type;
ds_vasos_od_w					oft_mapeamento_retina.ds_vasos_od%type;
ds_papila_oe_w					oft_mapeamento_retina.ds_papila_oe%type;
ds_vasos_oe_w					oft_mapeamento_retina.ds_vasos_oe%type;		
ds_macula_od_w					oft_mapeamento_retina.ds_macula_od%type;
ds_macula_oe_w					oft_mapeamento_retina.ds_macula_oe%type;
ds_retina_posterior_od_w	oft_mapeamento_retina.ds_retina_posterior_od%type;
ds_retina_posterior_oe_w	oft_mapeamento_retina.ds_retina_posterior_oe%type;
ds_retina_periferica_od_w	oft_mapeamento_retina.ds_retina_periferica_od%type;
ds_retina_periferica_oe_w	oft_mapeamento_retina.ds_retina_periferica_oe%type;
ds_observacao_od_w			oft_mapeamento_retina.ds_observacao_od%type;
ds_observacao_oe_w			oft_mapeamento_retina.ds_observacao_oe%type;
cd_profissional_w				oft_mapeamento_retina.cd_profissional%type;	
nm_usuario_w					usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w				varchar(1) := 'N';
ds_nervo_optico_od_w          oft_mapeamento_retina.ds_nervo_optico_od%type;
ds_nervo_optico_oe_w          oft_mapeamento_retina.ds_nervo_optico_oe%type;
ds_copa_disco_od_w            oft_mapeamento_retina.ds_copa_disco_od%type;
ds_copa_disco_oe_w            oft_mapeamento_retina.ds_copa_disco_oe%type;
ds_erro_w						varchar(4000);
											
BEGIN
begin
if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaMapeamentoRetina.count > 0) then
	for i in 1..vListaMapeamentoRetina.count loop
		begin
		if (vListaMapeamentoRetina[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaMapeamentoRetina[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaMapeamentoRetina[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w					:= vListaMapeamentoRetina[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 							:= pkg_date_utils.get_DateTime(vListaMapeamentoRetina[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w					:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'IE_NORMAL_OD' then
					ie_normal_od_w						:= vListaMapeamentoRetina[i].ds_valor;
					if (ie_normal_od_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;	
				when 'IE_NORMAL_OE' then
					ie_normal_oe_w						:= vListaMapeamentoRetina[i].ds_valor;
					if (ie_normal_oe_w = 'S') then
						ie_registrado_w	:= 'S';
					end if;
				when 'DS_VITREO_OD' then
					ds_vitreo_od_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_VITREO_OE' then
					ds_vitreo_oe_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_PAPILA_OD' then
					ds_papila_od_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_VASOS_OD' then
					ds_vasos_od_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_PAPILA_OE' then
					ds_papila_oe_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_VASOS_OE' then
					ds_vasos_oe_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_MACULA_OD' then
					ds_macula_od_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_MACULA_OE' then
					ds_macula_oe_w						:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_RETINA_POSTERIOR_OD' then
					ds_retina_posterior_od_w		:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_RETINA_POSTERIOR_OE' then
					ds_retina_posterior_oe_w		:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_RETINA_PERIFERICA_OD' then
					ds_retina_periferica_od_w		:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_RETINA_PERIFERICA_OE' then
					ds_retina_periferica_oe_w		:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_OBSERVACAO_OD' then
					ds_observacao_od_w				:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
				when 'DS_OBSERVACAO_OE' then
					ds_observacao_oe_w				:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
        when 'DS_NERVO_OPTICO_OD' then
					ds_nervo_optico_od_w			:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
        when 'DS_NERVO_OPTICO_OE' then
					ds_nervo_optico_oe_w			:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w					:= 'S';
        when 'DS_COPA_DISCO_OD' then
            ds_copa_disco_od_w		    := vListaMapeamentoRetina[i].ds_valor;
		  			ie_registrado_w			    	:= 'S';
        when 'DS_COPA_DISCO_OE' then
					ds_copa_disco_oe_w		    	:= vListaMapeamentoRetina[i].ds_valor;
					ie_registrado_w			      	:= 'S';
				else
					null;	
			end case;	
		end if;	
	end;
	end loop;
	
	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_mapeamento_retina
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;
	
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_mapeamento_retina
		set		dt_atualizacao				=	clock_timestamp(),
					nm_usuario					=	nm_usuario_w, 
					cd_profissional			= coalesce(cd_profissional_w,cd_profissional),	
					dt_registro					=	coalesce(dt_exame_w,dt_registro), 
					ds_observacao				=	ds_observacao_w,
					ie_normal_od				=	coalesce(ie_normal_od_w,ie_normal_od), 
					ie_normal_oe				=	coalesce(ie_normal_oe_w,ie_normal_oe), 
					ds_vitreo_od				=	ds_vitreo_od_w, 
					ds_vitreo_oe				=	ds_vitreo_oe_w, 
					ds_papila_od				=	ds_papila_od_w, 
					ds_vasos_od					=	ds_vasos_od_w, 
					ds_papila_oe				=	ds_papila_oe_w, 
					ds_vasos_oe					=	ds_vasos_oe_w, 
					ds_macula_od				=	ds_macula_od_w, 
					ds_macula_oe				=	ds_macula_oe_w, 
					ds_retina_posterior_od	=	ds_retina_posterior_od_w, 
					ds_retina_posterior_oe	=	ds_retina_posterior_oe_w, 
					ds_retina_periferica_od	=	ds_retina_periferica_od_w, 
					ds_retina_periferica_oe	=	ds_retina_periferica_oe_w, 
					ds_observacao_od			=	ds_observacao_od_w, 
					ds_observacao_oe			=	ds_observacao_oe_w,          
          ds_nervo_optico_od          =   ds_nervo_optico_od_w,                    
          ds_nervo_optico_oe          =   ds_nervo_optico_oe_w,                 
          ds_copa_disco_od            =   ds_copa_disco_od_w,                  
          ds_copa_disco_oe            =   ds_copa_disco_oe_w
		where		nr_sequencia				=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_mapeamento_retina_seq')
			into STRICT		nr_sequencia_w	
			;

			insert	into oft_mapeamento_retina(	nr_sequencia,
														dt_atualizacao, 
														nm_usuario, 
														dt_atualizacao_nrec, 
														nm_usuario_nrec, 
														cd_profissional, 
														dt_registro,
														nr_seq_consulta, 
														ie_situacao,
														ds_observacao,
														ie_normal_od, 
														ie_normal_oe, 
														ds_vitreo_od, 
														ds_vitreo_oe, 
														ds_papila_od, 
														ds_vasos_od, 
														ds_papila_oe, 
														ds_vasos_oe, 
														ds_macula_od, 
														ds_macula_oe, 
														ds_retina_posterior_od, 
														ds_retina_posterior_oe, 
														ds_retina_periferica_od, 
														ds_retina_periferica_oe, 
														ds_observacao_od, 
														ds_observacao_oe,
                            ds_nervo_optico_od,                                                        
                            ds_nervo_optico_oe,                            
                            ds_copa_disco_od,                                                        
                            ds_copa_disco_oe,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w, 
														clock_timestamp(), 
														nm_usuario_w, 
														clock_timestamp(), 
														nm_usuario_w, 
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')), 
														coalesce(dt_exame_w,clock_timestamp()), 
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														coalesce(ie_normal_od_w,'S'),
														coalesce(ie_normal_oe_w,'S'),
														ds_vitreo_od_w,
														ds_vitreo_oe_w,
														ds_papila_od_w,
														ds_vasos_od_w,
														ds_papila_oe_w,
														ds_vasos_oe_w,
														ds_macula_od_w,
														ds_macula_oe_w,
														ds_retina_posterior_od_w,
														ds_retina_posterior_oe_w,
														ds_retina_periferica_od_w,
														ds_retina_periferica_oe_w,
														ds_observacao_od_w,
														ds_observacao_oe_w,
                            ds_nervo_optico_od_w,                                                        
                            ds_nervo_optico_oe_w,                             
                            ds_copa_disco_od_w,                                                        
                            ds_copa_disco_oe_w,
														nr_seq_consulta_form_p);
														
			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_consulta_imagem(	nr_sequencia,
																dt_atualizacao, 
																nm_usuario, 
																dt_atualizacao_nrec, 
																nm_usuario_nrec, 
																ds_titulo, 
																ds_arquivo, 
																ds_arquivo_backup, 
																nr_seq_mapeamento)
																SELECT	nextval('oft_consulta_imagem_seq'),
																			clock_timestamp(), 
																			nm_usuario_w, 
																			clock_timestamp(), 
																			nm_usuario_w, 
																			ds_titulo, 
																			ds_arquivo, 
																			ds_arquivo_backup, 
																			nr_sequencia_w
																from		oft_consulta_imagem a,
																			oft_mapeamento_retina b
																where		a.nr_seq_mapeamento 		= 	b.nr_sequencia
																and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
																and		b.nr_seq_consulta			=	nr_seq_consulta_p;
																
				insert	into oft_imagem_exame(	nr_sequencia,
																dt_atualizacao, 
																nm_usuario, 
																dt_atualizacao_nrec, 
																nm_usuario_nrec, 
																ds_titulo, 
																ds_arquivo, 
																nr_seq_consulta, 
																ie_lado, 
																nr_seq_mapeamento, 
																ie_situacao, 
																cd_profissional)
													SELECT	nextval('oft_imagem_exame_seq'),
																clock_timestamp(), 
																nm_usuario_w, 
																clock_timestamp(), 
																nm_usuario_w, 
																ds_titulo, 
																ds_arquivo, 
																nr_seq_consulta_p, 
																ie_lado, 
																nr_sequencia_w, 
																'A', 
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C'))
													from		oft_imagem_exame a,
																oft_mapeamento_retina b
													where		a.nr_seq_mapeamento 		= 	b.nr_sequencia
													and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
													and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;														
		end if;												
	end if;											
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_mapeamento_retina ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaMapeamentoRetina strRecTypeFormOft) FROM PUBLIC;

