CREATE OR REPLACE PROCEDURE oft_insere_microscopia ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaMicroscopia strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w						oft_microscopia_especular.nr_sequencia%type;
dt_exame_w							oft_microscopia_especular.dt_registro%type;
ds_observacao_w					oft_microscopia_especular.ds_observacao%type;
qt_od_microscopia_w				oft_microscopia_especular.qt_od_microscopia%type;
qt_oe_microscopia_w				oft_microscopia_especular.qt_oe_microscopia%type;
qt_od_microscopia_sup_w			oft_microscopia_especular.qt_od_microscopia_sup%type;
qt_od_microscopia_temp_w		oft_microscopia_especular.qt_od_microscopia_temp%type;
qt_od_microscopia_central_w	oft_microscopia_especular.qt_od_microscopia_central%type;
qt_od_microscopia_nasal_w		oft_microscopia_especular.qt_od_microscopia_nasal%type;
qt_od_microscopia_inf_w			oft_microscopia_especular.qt_od_microscopia_inf%type;
qt_oe_microscopia_sup_w			oft_microscopia_especular.qt_oe_microscopia_sup%type;
qt_oe_microscopia_temp_w		oft_microscopia_especular.qt_oe_microscopia_temp%type;
qt_oe_microscopia_nasal_w		oft_microscopia_especular.qt_oe_microscopia_nasal%type;
qt_oe_microscopia_inf_w			oft_microscopia_especular.qt_oe_microscopia_inf%type;
qt_oe_microscopia_central_w	oft_microscopia_especular.qt_oe_microscopia_central%type;
qt_oe_espessura_sup_w			oft_microscopia_especular.qt_oe_espessura_sup%type;
qt_oe_espessura_nasal_w			oft_microscopia_especular.qt_oe_espessura_nasal%type;
qt_oe_espessura_central_w		oft_microscopia_especular.qt_oe_espessura_central%type;
qt_oe_espessura_temp_w			oft_microscopia_especular.qt_oe_espessura_temp%type;
qt_oe_espessura_inf_w			oft_microscopia_especular.qt_oe_espessura_inf%type;
qt_oe_espessura_w					oft_microscopia_especular.qt_oe_espessura%type;
qt_od_espessura_sup_w			oft_microscopia_especular.qt_od_espessura_sup%type;
qt_od_espessura_temp_w			oft_microscopia_especular.qt_od_espessura_temp%type;
qt_od_espessura_central_w		oft_microscopia_especular.qt_od_espessura_central%type;
qt_od_espessura_nasal_w			oft_microscopia_especular.qt_od_espessura_nasal%type;
qt_od_espessura_inf_w			oft_microscopia_especular.qt_od_espessura_inf%type;
qt_od_espessura_w					oft_microscopia_especular.qt_od_espessura%type;
cd_profissional_w					oft_microscopia_especular.cd_profissional%type;
nm_usuario_w						usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w					varchar(1) := 'N';
ds_erro_w							varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaMicroscopia.count > 0) then
	for i in 1..vListaMicroscopia.count loop
		begin
		if (vListaMicroscopia[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaMicroscopia[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaMicroscopia[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w							:= vListaMicroscopia[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 									:= pkg_date_utils.get_DateTime(vListaMicroscopia[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w							:= vListaMicroscopia[i].ds_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_MICROSCOPIA' then
					qt_od_microscopia_w						:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_MICROSCOPIA' then
					qt_oe_microscopia_w						:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_MICROSCOPIA_SUP' then
					qt_od_microscopia_sup_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_MICROSCOPIA_TEMP' then
					qt_od_microscopia_temp_w				:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_MICROSCOPIA_CENTRAL' then
					qt_od_microscopia_central_w			:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_MICROSCOPIA_NASAL' then
					qt_od_microscopia_nasal_w				:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_MICROSCOPIA_INF' then
					qt_od_microscopia_inf_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_MICROSCOPIA_SUP' then
					qt_oe_microscopia_sup_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_MICROSCOPIA_TEMP' then
					qt_oe_microscopia_temp_w				:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_MICROSCOPIA_NASAL' then
					qt_oe_microscopia_nasal_w				:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_MICROSCOPIA_INF' then
					qt_oe_microscopia_inf_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_MICROSCOPIA_CENTRAL' then
					qt_oe_microscopia_central_w			:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_ESPESSURA_SUP' then
					qt_oe_espessura_sup_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_ESPESSURA_NASAL' then
					qt_oe_espessura_nasal_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_ESPESSURA_CENTRAL' then
					qt_oe_espessura_central_w				:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_ESPESSURA_TEMP' then
					qt_oe_espessura_temp_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_ESPESSURA_INF' then
					qt_oe_espessura_inf_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_ESPESSURA' then
					qt_oe_espessura_w							:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_ESPESSURA_SUP' then
					qt_od_espessura_sup_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_ESPESSURA_TEMP' then
					qt_od_espessura_temp_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_ESPESSURA_CENTRAL' then
					qt_od_espessura_central_w				:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_ESPESSURA_NASAL' then
					qt_od_espessura_nasal_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_ESPESSURA_INF' then
					qt_od_espessura_inf_w					:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_ESPESSURA' then
					qt_od_espessura_w							:= vListaMicroscopia[i].nr_valor;
					ie_registrado_w							:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_microscopia_especular
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_microscopia_especular
		set		dt_atualizacao					=	clock_timestamp(),
					nm_usuario						=	nm_usuario_w,
					cd_profissional				= coalesce(cd_profissional_w,cd_profissional),
					dt_registro						=	coalesce(dt_exame_w,dt_registro),
					ds_observacao					=	ds_observacao_w,
					qt_od_microscopia				=	qt_od_microscopia_w,
					qt_oe_microscopia				=	qt_oe_microscopia_w,
					qt_od_microscopia_sup		=	qt_od_microscopia_sup_w,
					qt_od_microscopia_temp		=	qt_od_microscopia_temp_w,
					qt_od_microscopia_central	=	qt_od_microscopia_central_w,
					qt_od_microscopia_nasal		=	qt_od_microscopia_nasal_w,
					qt_od_microscopia_inf		=	qt_od_microscopia_inf_w,
					qt_oe_microscopia_sup		=	qt_oe_microscopia_sup_w,
					qt_oe_microscopia_temp		=	qt_oe_microscopia_temp_w,
					qt_oe_microscopia_nasal		=	qt_oe_microscopia_nasal_w,
					qt_oe_microscopia_inf		=	qt_oe_microscopia_inf_w,
					qt_oe_microscopia_central	=	qt_oe_microscopia_central_w,
					qt_oe_espessura_sup			=	qt_oe_espessura_sup_w,
					qt_oe_espessura_nasal		=	qt_oe_espessura_nasal_w,
					qt_oe_espessura_central		=	qt_oe_espessura_central_w,
					qt_oe_espessura_temp			=	qt_oe_espessura_temp_w,
					qt_oe_espessura_inf			=	qt_oe_espessura_inf_w,
					qt_oe_espessura				=	qt_oe_espessura_w,
					qt_od_espessura_sup			=	qt_od_espessura_sup_w,
					qt_od_espessura_temp			=	qt_od_espessura_temp_w,
					qt_od_espessura_central		=	qt_od_espessura_central_w,
					qt_od_espessura_nasal		=	qt_od_espessura_nasal_w,
					qt_od_espessura_inf			=	qt_od_espessura_inf_w,
					qt_od_espessura				=	qt_od_espessura_w
		where		nr_sequencia					=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_microscopia_especular_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_microscopia_especular(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_registro,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														qt_od_microscopia,
														qt_oe_microscopia,
														qt_od_microscopia_sup,
														qt_od_microscopia_temp,
														qt_od_microscopia_central,
														qt_od_microscopia_nasal,
														qt_od_microscopia_inf,
														qt_oe_microscopia_sup,
														qt_oe_microscopia_temp,
														qt_oe_microscopia_nasal,
														qt_oe_microscopia_inf,
														qt_oe_microscopia_central,
														qt_oe_espessura_sup,
														qt_oe_espessura_nasal,
														qt_oe_espessura_central,
														qt_oe_espessura_temp,
														qt_oe_espessura_inf,
														qt_oe_espessura,
														qt_od_espessura_sup,
														qt_od_espessura_temp,
														qt_od_espessura_central,
														qt_od_espessura_nasal,
														qt_od_espessura_inf,
														qt_od_espessura,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														qt_od_microscopia_w,
														qt_oe_microscopia_w,
														qt_od_microscopia_sup_w,
														qt_od_microscopia_temp_w,
														qt_od_microscopia_central_w,
														qt_od_microscopia_nasal_w,
														qt_od_microscopia_inf_w,
														qt_oe_microscopia_sup_w,
														qt_oe_microscopia_temp_w,
														qt_oe_microscopia_nasal_w,
														qt_oe_microscopia_inf_w,
														qt_oe_microscopia_central_w,
														qt_oe_espessura_sup_w,
														qt_oe_espessura_nasal_w,
														qt_oe_espessura_central_w,
														qt_oe_espessura_temp_w,
														qt_oe_espessura_inf_w,
														qt_oe_espessura_w,
														qt_od_espessura_sup_w,
														qt_od_espessura_temp_w,
														qt_od_espessura_central_w,
														qt_od_espessura_nasal_w,
														qt_od_espessura_inf_w,
														qt_od_espessura_w,
														nr_seq_consulta_form_p);

			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_imagem_exame(	nr_sequencia,
																dt_atualizacao,
																nm_usuario,
																dt_atualizacao_nrec,
																nm_usuario_nrec,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta,
																ie_lado,
																nr_seq_microscopia,
																ie_situacao,
																cd_profissional)
													SELECT	nextval('oft_imagem_exame_seq'),
																clock_timestamp(),
																nm_usuario_w,
																clock_timestamp(),
																nm_usuario_w,
																ds_titulo,
																ds_arquivo,
																nr_seq_consulta_p,
																ie_lado,
																nr_sequencia_w,
																'A',
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C'))
													from		oft_imagem_exame a,
																oft_microscopia_especular b
													where		a.nr_seq_microscopia		= 	b.nr_sequencia
													and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
													and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
