CREATE OR REPLACE PROCEDURE oft_insere_oculos ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, vListaOculos strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w			oft_oculos.nr_sequencia%type;
dt_exame_w				oft_oculos.dt_registro%type;
ds_observacao_w		oft_oculos.ds_observacao%type;
nr_seq_tipo_lente_w	oft_oculos.nr_seq_tipo_lente%type;
vl_od_pl_dio_esf_w	oft_oculos.vl_od_pl_dio_esf%type;
vl_od_pl_dio_cil_w	oft_oculos.vl_od_pl_dio_cil%type;
vl_od_pl_eixo_w		oft_oculos.vl_od_pl_eixo%type;
vl_od_pl_adicao_w		oft_oculos.vl_od_pl_adicao%type;
vl_oe_pl_dio_esf_w	oft_oculos.vl_oe_pl_dio_esf%type;
vl_oe_pl_dio_cil_w	oft_oculos.vl_oe_pl_dio_cil%type;
vl_oe_pl_eixo_w		oft_oculos.vl_oe_pl_eixo%type;
vl_oe_pl_adicao_w		oft_oculos.vl_oe_pl_adicao%type;
vl_od_pp_dio_esf_w	oft_oculos.vl_od_pp_dio_esf%type;
vl_od_pp_dio_cil_w	oft_oculos.vl_od_pp_dio_cil%type;
vl_od_pp_eixo_w		oft_oculos.vl_od_pp_eixo%type;
vl_od_pp_adicao_w		oft_oculos.vl_od_pp_adicao%type;
vl_oe_pp_dio_esf_w	oft_oculos.vl_oe_pp_dio_esf%type;
vl_oe_pp_dio_cil_w	oft_oculos.vl_oe_pp_dio_cil%type;
vl_oe_pp_eixo_w		oft_oculos.vl_oe_pp_eixo%type;
vl_oe_pp_adicao_w		oft_oculos.vl_oe_pp_adicao%type;
vl_od_in_dio_esf_w	oft_oculos.vl_od_in_dio_esf%type;
vl_od_in_dio_cil_w	oft_oculos.vl_od_in_dio_cil%type;
vl_oe_in_dio_esf_w	oft_oculos.vl_oe_in_dio_esf%type;
vl_oe_in_dio_cil_w	oft_oculos.vl_oe_in_dio_cil%type;
vl_oe_in_eixo_w		oft_oculos.vl_oe_in_eixo%type;
vl_oe_in_adicao_w		oft_oculos.vl_oe_in_adicao%type;
vl_od_in_eixo_w		oft_oculos.vl_od_in_eixo%type;
vl_od_in_adicao_w		oft_oculos.vl_od_in_adicao%type;
ds_orientacao_w		oft_oculos.ds_orientacao%type;
cd_profissional_w		oft_oculos.cd_profissional%type;
nm_usuario_w			usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w		varchar(1) := 'N';
ds_erro_w				varchar(4000);

BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaOculos.count > 0) then
	for i in 1..vListaOculos.count loop
		begin
		if (vListaOculos[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaOculos[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaOculos[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w							:= vListaOculos[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 									:= pkg_date_utils.get_DateTime(vListaOculos[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w							:= vListaOculos[i].ds_valor;
					ie_registrado_w							:= 'S';
				when 'NR_SEQ_TIPO_LENTE' then
					nr_seq_tipo_lente_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PL_DIO_ESF' then
					vl_od_pl_dio_esf_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PL_DIO_CIL' then
					vl_od_pl_dio_cil_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PL_EIXO' then
					vl_od_pl_eixo_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PL_ADICAO' then
					vl_od_pl_adicao_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PL_DIO_ESF' then
					vl_oe_pl_dio_esf_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PL_DIO_CIL' then
					vl_oe_pl_dio_cil_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PL_EIXO' then
					vl_oe_pl_eixo_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PL_ADICAO' then
					vl_oe_pl_adicao_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PP_DIO_ESF' then
					vl_od_pp_dio_esf_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PP_DIO_CIL' then
					vl_od_pp_dio_cil_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PP_EIXO' then
					vl_od_pp_eixo_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_PP_ADICAO' then
					vl_od_pp_adicao_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PP_DIO_ESF' then
					vl_oe_pp_dio_esf_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PP_DIO_CIL' then
					vl_oe_pp_dio_cil_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PP_EIXO' then
					vl_oe_pp_eixo_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_PP_ADICAO' then
					vl_oe_pp_adicao_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_IN_DIO_ESF' then
					vl_od_in_dio_esf_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_IN_DIO_CIL' then
					vl_od_in_dio_cil_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_IN_DIO_ESF' then
					vl_oe_in_dio_esf_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_IN_DIO_CIL' then
					vl_oe_in_dio_cil_w						:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_IN_EIXO' then
					vl_oe_in_eixo_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OE_IN_ADICAO' then
					vl_oe_in_adicao_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_IN_EIXO' then
					vl_od_in_eixo_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'VL_OD_IN_ADICAO' then
					vl_od_in_adicao_w							:= vListaOculos[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'DS_ORIENTACAO' then
					ds_orientacao_w							:= vListaOculos[i].ds_valor;
					ie_registrado_w							:= 'S';
				else
					null;
			end case;
		end if;
	end;
	end loop;

	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_oculos
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;

	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_oculos
		set		dt_atualizacao 	=	clock_timestamp(),
					nm_usuario 			= 	nm_usuario_w,
					cd_profissional	= coalesce(cd_profissional_w,cd_profissional),
					dt_registro			=	coalesce(dt_exame_w,dt_registro),
					ds_observacao		=	ds_observacao_w,
					nr_seq_tipo_lente = 	nr_seq_tipo_lente_w,
					vl_od_pl_dio_esf 	= 	vl_od_pl_dio_esf_w,
					vl_od_pl_dio_cil 	= 	vl_od_pl_dio_cil_w,
					vl_od_pl_eixo 		= 	vl_od_pl_eixo_w,
					vl_od_pl_adicao 	= 	vl_od_pl_adicao_w,
					vl_oe_pl_dio_esf 	= 	vl_oe_pl_dio_esf_w,
					vl_oe_pl_dio_cil 	= 	vl_oe_pl_dio_cil_w,
					vl_oe_pl_eixo 		= 	vl_oe_pl_eixo_w,
					vl_oe_pl_adicao 	= 	vl_oe_pl_adicao_w,
					vl_od_pp_dio_esf 	= 	vl_od_pp_dio_esf_w,
					vl_od_pp_dio_cil 	= 	vl_od_pp_dio_cil_w,
					vl_od_pp_eixo 		= 	vl_od_pp_eixo_w,
					vl_od_pp_adicao 	= 	vl_od_pp_adicao_w,
					vl_oe_pp_dio_esf 	= 	vl_oe_pp_dio_esf_w,
					vl_oe_pp_dio_cil 	= 	vl_oe_pp_dio_cil_w,
					vl_oe_pp_eixo 		= 	vl_oe_pp_eixo_w,
					vl_oe_pp_adicao 	= 	vl_oe_pp_adicao_w,
					vl_od_in_dio_esf 	= 	vl_od_in_dio_esf_w,
					vl_od_in_dio_cil 	= 	vl_od_in_dio_cil_w,
					vl_oe_in_dio_esf 	= 	vl_oe_in_dio_esf_w,
					vl_oe_in_dio_cil 	= 	vl_oe_in_dio_cil_w,
					vl_oe_in_eixo 		= 	vl_oe_in_eixo_w,
					vl_oe_in_adicao 	= 	vl_oe_in_adicao_w,
					vl_od_in_eixo 		= 	vl_od_in_eixo_w,
					vl_od_in_adicao 	= 	vl_od_in_adicao_w,
					ds_orientacao		=	ds_orientacao_w
		where		nr_sequencia		=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_oculos_seq')
			into STRICT		nr_sequencia_w
			;

			insert	into oft_oculos(	nr_sequencia,
														dt_atualizacao,
														nm_usuario,
														dt_atualizacao_nrec,
														nm_usuario_nrec,
														cd_profissional,
														dt_registro,
														nr_seq_consulta,
														ie_situacao,
														ds_observacao,
														nr_seq_tipo_lente,
														vl_od_pl_dio_esf,
														vl_od_pl_dio_cil,
														vl_od_pl_eixo,
														vl_od_pl_adicao,
														vl_oe_pl_dio_esf,
														vl_oe_pl_dio_cil,
														vl_oe_pl_eixo,
														vl_oe_pl_adicao,
														vl_od_pp_dio_esf,
														vl_od_pp_dio_cil,
														vl_od_pp_eixo,
														vl_od_pp_adicao,
														vl_oe_pp_dio_esf,
														vl_oe_pp_dio_cil,
														vl_oe_pp_eixo,
														vl_oe_pp_adicao,
														vl_od_in_dio_esf,
														vl_od_in_dio_cil,
														vl_oe_in_dio_esf,
														vl_oe_in_dio_cil,
														vl_oe_in_eixo,
														vl_oe_in_adicao,
														vl_od_in_eixo,
														vl_od_in_adicao,
														ds_orientacao,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w,
														clock_timestamp(),
														nm_usuario_w,
														clock_timestamp(),
														nm_usuario_w,
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')),
														coalesce(dt_exame_w,clock_timestamp()),
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														nr_seq_tipo_lente_w,
														vl_od_pl_dio_esf_w,
														vl_od_pl_dio_cil_w,
														vl_od_pl_eixo_w,
														vl_od_pl_adicao_w,
														vl_oe_pl_dio_esf_w,
														vl_oe_pl_dio_cil_w,
														vl_oe_pl_eixo_w,
														vl_oe_pl_adicao_w,
														vl_od_pp_dio_esf_w,
														vl_od_pp_dio_cil_w,
														vl_od_pp_eixo_w,
														vl_od_pp_adicao_w,
														vl_oe_pp_dio_esf_w,
														vl_oe_pp_dio_cil_w,
														vl_oe_pp_eixo_w,
														vl_oe_pp_adicao_w,
														vl_od_in_dio_esf_w,
														vl_od_in_dio_cil_w,
														vl_oe_in_dio_esf_w,
														vl_oe_in_dio_cil_w,
														vl_oe_in_eixo_w,
														vl_oe_in_adicao_w,
														vl_od_in_eixo_w,
														vl_od_in_adicao_w,
														ds_orientacao_w,
														nr_seq_consulta_form_p);
		end if;
	end if;
end if;

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
