CREATE OR REPLACE PROCEDURE oft_insere_paquimetria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaPaquimetria strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w					oft_paquimetria.nr_sequencia%type;
dt_exame_w						oft_paquimetria.dt_registro%type;
ds_observacao_w				oft_paquimetria.ds_observacao%type;
qt_od_paqui_sup_w				oft_paquimetria.qt_od_paqui_sup%type;
qt_od_paqui_temp_w			oft_paquimetria.qt_od_paqui_temp%type;
qt_od_paqui_inf_w				oft_paquimetria.qt_od_paqui_inf%type;
qt_od_paqui_nasal_w			oft_paquimetria.qt_od_paqui_nasal%type;
qt_od_paqui_central_w		oft_paquimetria.qt_od_paqui_central%type;
qt_oe_paqui_sup_w				oft_paquimetria.qt_oe_paqui_sup%type;
qt_oe_paqui_temp_w			oft_paquimetria.qt_oe_paqui_temp%type;
qt_oe_paqui_inf_w				oft_paquimetria.qt_oe_paqui_inf%type;
qt_oe_paqui_nasal_w			oft_paquimetria.qt_oe_paqui_nasal%type;
qt_oe_paqui_central_w		oft_paquimetria.qt_oe_paqui_central%type;
qt_avg_od_w						oft_paquimetria.qt_avg_od%type;
qt_avg_oe_w						oft_paquimetria.qt_avg_oe%type;
qt_od_paqui_temp_sup_w		oft_paquimetria.qt_od_paqui_temp_sup%type;
qt_od_paqui_nasal_sup_w		oft_paquimetria.qt_od_paqui_nasal_sup%type;
qt_od_paqui_temp_inf_w		oft_paquimetria.qt_od_paqui_temp_inf%type;
qt_od_paqui_nasal_inf_w		oft_paquimetria.qt_od_paqui_nasal_inf%type;
qt_oe_paqui_temp_sup_w		oft_paquimetria.qt_oe_paqui_temp_sup%type;
qt_oe_paqui_nasal_sup_w		oft_paquimetria.qt_oe_paqui_nasal_sup%type;
qt_oe_paqui_temp_inf_w		oft_paquimetria.qt_oe_paqui_temp_inf%type;
qt_oe_paqui_nasal_inf_w		oft_paquimetria.qt_oe_paqui_nasal_inf%type;	
ie_tipo_paquimetria_w		oft_paquimetria.ie_tipo_paquimetria%type;
cd_profissional_w				oft_paquimetria.cd_profissional%type;	
nm_usuario_w					usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w				varchar(1) := 'N';
ds_erro_w						varchar(4000);
											
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaPaquimetria.count > 0) then
	for i in 1..vListaPaquimetria.count loop
		begin
		
		if (vListaPaquimetria[i].nr_seq_visao = 86144) then
			ie_tipo_paquimetria_w := 'U';
		elsif (vListaPaquimetria[i].nr_seq_visao = 86143) then
			ie_tipo_paquimetria_w := 'S';
		end if;	
		
		if (vlistapaquimetria[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vlistapaquimetria[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			
			case upper(vListaPaquimetria[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w							:= vListaPaquimetria[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 									:= pkg_date_utils.get_DateTime(vListaPaquimetria[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w							:= vListaPaquimetria[i].ds_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_SUP' then
					qt_od_paqui_sup_w							:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_TEMP' then
					qt_od_paqui_temp_w						:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_INF' then
					qt_od_paqui_inf_w							:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_NASAL' then
					qt_od_paqui_nasal_w						:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_CENTRAL' then
					qt_od_paqui_central_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_SUP' then
					qt_oe_paqui_sup_w							:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_TEMP' then
					qt_oe_paqui_temp_w						:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_INF' then
					qt_oe_paqui_inf_w							:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_NASAL' then
					qt_oe_paqui_nasal_w						:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_CENTRAL' then
					qt_oe_paqui_central_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_AVG_OD' then
					qt_avg_od_w									:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_AVG_OE' then
					qt_avg_oe_w									:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_TEMP_SUP' then
					qt_od_paqui_temp_sup_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_NASAL_SUP' then
					qt_od_paqui_nasal_sup_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_TEMP_INF' then
					qt_od_paqui_temp_inf_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PAQUI_NASAL_INF' then
					qt_od_paqui_nasal_inf_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_TEMP_SUP' then
					qt_oe_paqui_temp_sup_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_NASAL_SUP' then
					qt_oe_paqui_nasal_sup_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_TEMP_INF' then
					qt_oe_paqui_temp_inf_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PAQUI_NASAL_INF' then
					qt_oe_paqui_nasal_inf_w					:= vListaPaquimetria[i].nr_valor;
					ie_registrado_w							:= 'S';
				else
					null;	
			end case;	
		end if;	
	end;
	end loop;
	
	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_paquimetria
	where		nr_seq_consulta_form 			= nr_seq_consulta_form_p
	and		nr_seq_consulta					= nr_seq_consulta_p
	and		coalesce(ie_tipo_paquimetria,'S')	= ie_tipo_paquimetria_w
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario							= nm_usuario_w;
	
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_paquimetria
		set		dt_atualizacao				=	clock_timestamp(),
					nm_usuario					=	nm_usuario_w, 
					cd_profissional			= coalesce(cd_profissional_w,cd_profissional),	
					dt_registro					=	coalesce(dt_exame_w,dt_registro), 
					ds_observacao				=	ds_observacao_w,
					qt_od_paqui_sup			=	qt_od_paqui_sup_w, 
					qt_od_paqui_temp			=	qt_od_paqui_temp_w, 
					qt_od_paqui_inf			=	qt_od_paqui_inf_w, 
					qt_od_paqui_nasal			=	qt_od_paqui_nasal_w, 
					qt_od_paqui_central		=	qt_od_paqui_central_w, 
					qt_oe_paqui_sup			=	qt_oe_paqui_sup_w, 
					qt_oe_paqui_temp			=	qt_oe_paqui_temp_w, 
					qt_oe_paqui_inf			=	qt_oe_paqui_inf_w, 
					qt_oe_paqui_nasal			=	qt_oe_paqui_nasal_w, 
					qt_oe_paqui_central		=	qt_oe_paqui_central_w, 
					qt_avg_od					=	qt_avg_od_w, 
					qt_avg_oe					=	qt_avg_oe_w, 
					qt_od_paqui_temp_sup		=	qt_od_paqui_temp_sup_w, 
					qt_od_paqui_nasal_sup	=	qt_od_paqui_nasal_sup_w, 
					qt_od_paqui_temp_inf		=	qt_od_paqui_temp_inf_w, 
					qt_od_paqui_nasal_inf	=	qt_od_paqui_nasal_inf_w, 
					qt_oe_paqui_temp_sup		=	qt_oe_paqui_temp_sup_w, 
					qt_oe_paqui_nasal_sup	=	qt_oe_paqui_nasal_sup_w, 
					qt_oe_paqui_temp_inf		=	qt_oe_paqui_temp_inf_w, 
					qt_oe_paqui_nasal_inf	=	qt_oe_paqui_nasal_inf_w
		where		nr_sequencia				=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_paquimetria_seq')
			into STRICT		nr_sequencia_w	
			;

			insert	into oft_paquimetria(	nr_sequencia,
														dt_atualizacao, 
														nm_usuario, 
														dt_atualizacao_nrec, 
														nm_usuario_nrec, 
														cd_profissional, 
														dt_registro,
														nr_seq_consulta, 
														ie_situacao,
														ds_observacao,
														qt_od_paqui_sup, 
														qt_od_paqui_temp, 
														qt_od_paqui_inf, 
														qt_od_paqui_nasal, 
														qt_od_paqui_central, 
														qt_oe_paqui_sup, 
														qt_oe_paqui_temp, 
														qt_oe_paqui_inf, 
														qt_oe_paqui_nasal, 
														qt_oe_paqui_central, 
														qt_avg_od, 
														qt_avg_oe, 
														qt_od_paqui_temp_sup, 
														qt_od_paqui_nasal_sup, 
														qt_od_paqui_temp_inf, 
														qt_od_paqui_nasal_inf, 
														qt_oe_paqui_temp_sup, 
														qt_oe_paqui_nasal_sup, 
														qt_oe_paqui_temp_inf, 
														qt_oe_paqui_nasal_inf, 
														ie_tipo_paquimetria,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w, 
														clock_timestamp(), 
														nm_usuario_w, 
														clock_timestamp(), 
														nm_usuario_w, 
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')), 
														coalesce(dt_exame_w,clock_timestamp()), 
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														qt_od_paqui_sup_w,
														qt_od_paqui_temp_w,
														qt_od_paqui_inf_w,
														qt_od_paqui_nasal_w,
														qt_od_paqui_central_w,
														qt_oe_paqui_sup_w,
														qt_oe_paqui_temp_w,
														qt_oe_paqui_inf_w,
														qt_oe_paqui_nasal_w,
														qt_oe_paqui_central_w,
														qt_avg_od_w,
														qt_avg_oe_w,
														qt_od_paqui_temp_sup_w,
														qt_od_paqui_nasal_sup_w,
														qt_od_paqui_temp_inf_w,
														qt_od_paqui_nasal_inf_w,
														qt_oe_paqui_temp_sup_w,
														qt_oe_paqui_nasal_sup_w,
														qt_oe_paqui_temp_inf_w,
														qt_oe_paqui_nasal_inf_w,
														ie_tipo_paquimetria_w,
														nr_seq_consulta_form_p);
														
			if (coalesce(nr_seq_consulta_form_ant_p,0) > 0) then
				insert	into oft_imagem_exame(	nr_sequencia,
																dt_atualizacao, 
																nm_usuario, 
																dt_atualizacao_nrec, 
																nm_usuario_nrec, 
																ds_titulo, 
																ds_arquivo, 
																nr_seq_consulta, 
																ie_lado, 
																nr_seq_paquimetria, 
																ie_situacao, 
																cd_profissional)
													SELECT	nextval('oft_imagem_exame_seq'),
																clock_timestamp(), 
																nm_usuario_w, 
																clock_timestamp(), 
																nm_usuario_w, 
																ds_titulo, 
																ds_arquivo, 
																nr_seq_consulta_p, 
																ie_lado, 
																nr_sequencia_w, 
																'A', 
																coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C'))
													from		oft_imagem_exame a,
																oft_paquimetria b
													where		a.nr_seq_paquimetria		= 	b.nr_sequencia
													and		b.nr_seq_consulta_form	= 	nr_seq_consulta_form_ant_p
													and		b.nr_seq_consulta			=	nr_seq_consulta_p;
			end if;														
		end if;												
	end if;											
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
