-- REVOKE ALL ON PROCEDURE oft_insere_paquimetria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, nr_seq_consulta_form_ant_p bigint, vListaPaquimetria strRecTypeFormOft) FROM PUBLIC;

