SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE oft_insere_pupilometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, vListaPupilometria strRecTypeFormOft) AS $body$
DECLARE


nr_sequencia_w			oft_pupilometria.nr_sequencia%type;
dt_exame_w				oft_pupilometria.dt_registro%type;
ds_observacao_w		oft_pupilometria.ds_observacao%type;
qt_od_pupilometria_w	oft_pupilometria.qt_od_pupilometria%type;
qt_oe_pupilometria_w	oft_pupilometria.qt_oe_pupilometria%type;
ie_olho_dominante_w	oft_pupilometria.ie_olho_dominante%type;
qt_tamanho_corneano_w	oft_pupilometria.qt_tamanho_corneano%type;
cd_profissional_w		oft_pupilometria.cd_profissional%type;	
nm_usuario_w			usuario.nm_usuario%type := wheb_usuario_pck.get_nm_usuario;
ie_registrado_w		varchar(1) := 'N';
ds_erro_w				varchar(4000);
											
BEGIN
begin

if (coalesce(nr_seq_consulta_p,0) > 0) and (vListaPupilometria.count > 0) then
	for i in 1..vListaPupilometria.count loop
		begin
		if (vListaPupilometria[i](.ds_valor IS NOT NULL AND .ds_valor::text <> '')) or (vListaPupilometria[i](.nr_valor IS NOT NULL AND .nr_valor::text <> '')) then
			case upper(vListaPupilometria[i].nm_campo)
				when 'CD_PROFISSIONAL' then
					cd_profissional_w							:= vListaPupilometria[i].ds_valor;
				when 'DT_REGISTRO' then
					dt_exame_w 									:= pkg_date_utils.get_DateTime(vListaPupilometria[i].ds_valor);
				when 'DS_OBSERVACAO' then
					ds_observacao_w							:= vListaPupilometria[i].ds_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OD_PUPILOMETRIA' then
					qt_od_pupilometria_w						:= vListaPupilometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'QT_OE_PUPILOMETRIA' then
					qt_oe_pupilometria_w						:= vListaPupilometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				when 'IE_OLHO_DOMINANTE' then
					ie_olho_dominante_w						:= vListaPupilometria[i].ds_valor;
					ie_registrado_w							:= 'S';
				when 'QT_TAMANHO_CORNEANO' then
					qt_tamanho_corneano_w					:= vListaPupilometria[i].nr_valor;
					ie_registrado_w							:= 'S';
				else
					null;	
			end case;	
		end if;	
	end;
	end loop;
	
	select	max(nr_sequencia)
	into STRICT		nr_sequencia_w
	from		oft_pupilometria
	where		nr_seq_consulta_form = nr_seq_consulta_form_p
	and		nr_seq_consulta		= nr_seq_consulta_p
	and		coalesce(dt_liberacao::text, '') = ''
	and		nm_usuario				= nm_usuario_w;
	
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
		update	oft_pupilometria
		set		dt_atualizacao			=	clock_timestamp(),
					nm_usuario				=	nm_usuario_w,
					cd_profissional		= coalesce(cd_profissional_w,cd_profissional),	
					dt_registro				=	coalesce(dt_exame_w,dt_registro),
					ds_observacao			=	ds_observacao_w,
					qt_od_pupilometria	=	qt_od_pupilometria_w,
					qt_oe_pupilometria	=	qt_oe_pupilometria_w,
					ie_olho_dominante		=	ie_olho_dominante_w,
					qt_tamanho_corneano	=	qt_tamanho_corneano_w
		where		nr_sequencia			=	nr_sequencia_w;
		CALL wheb_usuario_pck.set_ie_commit('S');
	else
		if (ie_registrado_w = 'S') then
			CALL wheb_usuario_pck.set_ie_commit('S');
			select	nextval('oft_pupilometria_seq')
			into STRICT		nr_sequencia_w	
			;

			insert	into oft_pupilometria(	nr_sequencia,
														dt_atualizacao, 
														nm_usuario, 
														dt_atualizacao_nrec, 
														nm_usuario_nrec, 
														cd_profissional, 
														dt_registro,
														nr_seq_consulta, 
														ie_situacao,
														ds_observacao,
														qt_od_pupilometria, 
														qt_oe_pupilometria, 
														ie_olho_dominante, 
														qt_tamanho_corneano,
														nr_seq_consulta_form
														)
			values (	nr_sequencia_w, 
														clock_timestamp(), 
														nm_usuario_w, 
														clock_timestamp(), 
														nm_usuario_w, 
														coalesce(cd_profissional_w,obter_pf_usuario(nm_usuario_w,'C')), 
														coalesce(dt_exame_w,clock_timestamp()), 
														nr_seq_consulta_p,
														'A',
														ds_observacao_w,
														qt_od_pupilometria_w,
														qt_oe_pupilometria_w,
														ie_olho_dominante_w,
														qt_tamanho_corneano_w,
														nr_seq_consulta_form_p);
		end if;												
	end if;											
end if;	

exception
when others then
	ds_erro_w	:= substr(sqlerrm,1,4000);
	update	OFT_CONSULTA_FORMULARIO
	set		ds_stack			=	substr(dbms_utility.format_call_stack||ds_erro_w,1,4000)
	where		nr_sequencia	= 	nr_seq_consulta_form_p;
end;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE oft_insere_pupilometria ( nr_seq_consulta_p bigint, nr_seq_consulta_form_p bigint, vListaPupilometria strRecTypeFormOft) FROM PUBLIC;

