	  if (vl_oe_pl_ard_esf_w IS NOT NULL AND vl_oe_pl_ard_esf_w::text <> '') then
		vl_oe_pp_ard_esf_w   := vl_oe_pl_ard_esf_w + vl_adicao_w;
	  end if;
	  if (ie_replica_campos_w   = 'S') then
		if (vl_od_pl_ard_cil_w IS NOT NULL AND vl_od_pl_ard_cil_w::text <> '') then
		  vl_od_pp_ard_cil_w   := vl_od_pl_ard_cil_w;
		end if;
		if (vl_oe_pl_ard_cil_w IS NOT NULL AND vl_oe_pl_ard_cil_w::text <> '') then
		  vl_oe_pp_ard_cil_w   := vl_oe_pl_ard_cil_w;
		end if;
	  end if;
	else
	  vl_od_pp_ard_esf_w := null;
	  vl_oe_pp_ard_esf_w := null;
	  vl_od_pp_ard_cil_w := null;
	  vl_oe_pp_ard_cil_w := null;
	end if;
   
	if (nr_sequencia_w IS NOT NULL AND nr_sequencia_w::text <> '') then
